CREATE FUNCTION [IMS].[fnCCCU_NumberToWords] (@n varchar(20))
RETURNS VARCHAR(255)
AS
BEGIN
	DECLARE @i int, @temp char(1),  @s VARCHAR(20), @result VARCHAR(255)
	SET @s=convert(varchar(20), @n)
	SET @i=LEN(@s)
	SET @result=''
	
	WHILE (@i>0)
	BEGIN
		SET @temp=(SUBSTRING(@s,@i,1))
		IF ((LEN(@s)-@i) % 3)=1
		IF @temp='1'
		SET @result=CASE (SUBSTRING(@s,@i+1,1))
			WHEN '0' THEN 'Ten'
			WHEN '1' THEN 'Eleven'
			WHEN '2' THEN 'Twelve'
			WHEN '3' THEN 'Thirteen'
			WHEN '4' THEN 'Fourteen'
			WHEN '5' THEN 'Fifteen'
			WHEN '6' THEN 'Sixteen'
			WHEN '7' THEN 'Seventeen'
			WHEN '8' THEN 'Eighteen'
			WHEN '9' THEN 'Nineteen'
			END+' '+CASE
					WHEN ((LEN(@s)-@i)=4) THEN 'Thousand '
					WHEN ((LEN(@s)-@i)=7) THEN 'Million '
					WHEN ((LEN(@s)-@i)=10) THEN 'Billion '
					WHEN ((LEN(@s)-@i)=13) THEN 'Trillion '
					WHEN ((LEN(@s)-@i)=16) THEN 'Quadrillion '
					ELSE ''
					END+@result
		ELSE
		BEGIN
			SET @result=CASE (SUBSTRING(@s,@i+1,1))
				WHEN '0' THEN ''
				WHEN '1' THEN 'One'
				WHEN '2' THEN 'Two'
				WHEN '3' THEN 'Three'
				WHEN '4' THEN 'Four'
				WHEN '5' THEN 'Five'
				WHEN '6' THEN 'Six'
				WHEN '7' THEN 'Seven'
				WHEN '8' THEN 'Eight'
				WHEN '9' THEN 'Nine'
				END+' '+ CASE
					WHEN ((LEN(@s)-@i)=4) THEN 'Thousand '
					WHEN ((LEN(@s)-@i)=7) THEN 'Million '
					WHEN ((LEN(@s)-@i)=10) THEN 'Billion '
					WHEN ((LEN(@s)-@i)=13) THEN 'Trillion '
					WHEN ((LEN(@s)-@i)=16) THEN 'Quadrillion '
					ELSE ''
					END+@result
			SET @result=CASE @temp
				WHEN '0' THEN ''
				WHEN '1' THEN 'Ten'
				WHEN '2' THEN 'Twenty'
				WHEN '3' THEN 'Thirty'
				WHEN '4' THEN 'Forty'
				WHEN '5' THEN 'Fifty'
				WHEN '6' THEN 'Sixty'
				WHEN '7' THEN 'Seventy'
				WHEN '8' THEN 'Eighty'
				WHEN '9' THEN 'Ninety'
				END+' '+@result
		END
		IF (((LEN(@s)-@i) % 3)=2) OR (((LEN(@s)-@i) % 3)=0) AND (@i=1)
		BEGIN
		SET @result=CASE @temp
			WHEN '0' THEN ''
			WHEN '1' THEN 'One'
			WHEN '2' THEN 'Two'
			WHEN '3' THEN 'Three'
			WHEN '4' THEN 'Four'
			WHEN '5' THEN 'Five'
			WHEN '6' THEN 'Six'
			WHEN '7' THEN 'Seven'
			WHEN '8' THEN 'Eight'
			WHEN '9' THEN 'Nine'
			END +' '+CASE
				WHEN (@s='0') THEN 'Zero'
				WHEN (@temp<>'0')AND( ((LEN(@s)-@i) % 3)=2) THEN 'Hundred '
				ELSE ''
				END + CASE
				WHEN ((LEN(@s)-@i)=3) THEN 'Thousand '
				WHEN ((LEN(@s)-@i)=6) THEN 'Million '
				WHEN ((LEN(@s)-@i)=9) THEN 'Billion '
				WHEN ((LEN(@s)-@i)=12) THEN 'Trillion '
				WHEN ((LEN(@s)-@i)=15) THEN 'Quadrillion '
				ELSE ''
					END+ @result
		END
		SET @i=@i-1
	END
	SET @result = REPLACE(REPLACE(REPLACE(@result,'  ',' '),'  ',' '),'  ',' ')
	RETURN @result
END
