CREATE FUNCTION [IMS].[fnSafe_GetCartItems] (@TrackingNum INT, @SLType1 CHAR(1), @SLType2 CHAR(1), @SLType3 CHAR(1), @SLType4 CHAR(1), @SLType5 CHAR(1), @SLType6 CHAR(1), @Status CHAR(1), @YesNoMaybe CHAR(10)) 
