	, CASE WHEN JO.SSN <> '' THEN RTRIM(LEFT(JO.SSN, 3) + '-' + LEFT(RIGHT(JO.SSN, 6), 2) + '-' + RIGHT(JO.SSN, 4)) END AS SSN
	, JO.Email1 AS [Email]
