CREATE procedure [IMS].[sp_GetHttpSettings]
@scriptId varchar(100)
AS
BEGIN

	select 
	name = isnull(s.name, d.name),
	value = isnull(s.value, d.value),
	id = isnull(s.id, d.id)

	from
	(
		select s.name, s.value, s.id
		from ims.HttpSetting s
		where s.scriptId = @scriptId
	) s
	full outer join 
	(
		select s.name, s.value, s.id
		from ims.HttpSetting s
		where s.scriptId = '*'
	) d on s.name = d.name
	


END
