						  NMRFunding ON NMRFundingBank.FundingID = NMRFunding.ID
		WHERE     (NMRFunding.AccountNumber = @AccountNumber AND DateTimeVerificationReconciled IS NULL AND NMRFundingBank.VerificationStatus = 'W')       
    END	
END


