CREATE PROCEDURE [IMS].[spAPGFCU_D01Fields]  @TrackingNum INTEGER, @DocIndex INTEGER, @ApplicationMode VARCHAR(50) = NULL, @Param1 VARCHAR(50) = NULL, @Param2 VARCHAR(50) = NULL AS

