	, JOJE2.WPhoneAC AS JE2WorkAC
	, LEFT(JOJE2.WPhone, 3) + '-' + RIGHT(RTRIM(JOJE2.WPhone), 4) AS JE2WorkPhone
	, JOJE2.DOB AS JE2DOB
