	WHERE FT.DateTimeLastTransaction IS NULL AND 
		FT.FundingType = 'B' AND
		F.AccountNumber = @NMRAccountNumber	 
	GROUP BY FB.ID	
	  
	SELECT  FRPT.* FROM
