CREATE Procedure [IMS].[spOFCU_SignOnOAO]
@TrackingNum int,
@Password varchar(24),
@SSN varchar(9),
@DOB datetime
AS
BEGIN
	SELECT
		n.TrackingNum, 
		ISNULL(CAST(n.Account AS VARCHAR(50)),'') AS Account, 
		n.UserSessionXML, 
		n.AccountType, 
		n.Created, 
		n.Processed, 
		n.TNExpireDate, 
		e.BadLoginAttempts
	FROM IMS.NewMemberRequest n
	JOIN IMS.NewMemberRequestExtra e ON n.TrackingNum = e.TrackingNum
	WHERE 
