	WHERE BE.TrackingNum = @TrackingNum AND (BE.BType IN ('IB', 'IC')) AND SA.SLType = 'I' AND SA.ProductType LIKE 'IRA%' AND SA.Description NOT LIKE '%ROTH%' AND
