		END
		IF @joseqcount > 4
		BEGIN
			INSERT @SuffixList ([JOType], [Suffixes]) VALUES ('JO', @Suffixes)
		END
		IF @joseqcount > 6
		BEGIN
			INSERT @SuffixList ([JOType], [Suffixes]) VALUES ('JO', @Suffixes)
		END
		IF @joseqcount > 8
		BEGIN
			INSERT @SuffixList ([JOType], [Suffixes]) VALUES ('JO', @Suffixes)
		END
		IF @joseqcount > 10
		BEGIN
			INSERT @SuffixList ([JOType], [Suffixes]) VALUES ('JO', @Suffixes)
		END
	END

	FETCH NEXT FROM mycur1
	INTO @JOSeqList, @JList
