		SET @count = @count + 1
		SET @pagecount = @pagecount + 1
		IF @count = @DocIndex
		BEGIN
			SET @totalpages = (@joseqcount + 1) / 2
			SET @pagenumber = @pagecount
			SET @SuffixFilter = RTRIM(@Suffixes)
			SET @JFilter = RTRIM(@JList)
		END
	END
	IF @joseqcount > 4
	BEGIN
		SET @JList = SUBSTRING(@JList, CHARINDEX(',', @JList, CHARINDEX(',', @JList) + 1) + 2, LEN(@JList))
		SET @count = @count + 1
		SET @pagecount = @pagecount + 1
		IF @count = @DocIndex
		BEGIN
			SET @totalpages = (@joseqcount + 1) / 2
			SET @pagenumber = @pagecount
			SET @SuffixFilter = RTRIM(@Suffixes)
			SET @JFilter = RTRIM(@JList)
		END
	END
	IF @joseqcount > 6
	BEGIN
		SET @JList = SUBSTRING(@JList, CHARINDEX(',', @JList, CHARINDEX(',', @JList) + 1) + 2, LEN(@JList))
		SET @count = @count + 1
		IF @count = @DocIndex
		BEGIN
			SET @totalpages = (@joseqcount + 1) / 2
			SET @pagenumber = @pagecount
			SET @SuffixFilter = RTRIM(@Suffixes)
			SET @JFilter = RTRIM(@JList)
		END
	END
	IF @joseqcount > 8
	BEGIN
		SET @JList = SUBSTRING(@JList, CHARINDEX(',', @JList, CHARINDEX(',', @JList) + 1) + 2, LEN(@JList))
		SET @count = @count + 1
		IF @count = @DocIndex
		BEGIN
			SET @totalpages = (@joseqcount + 1) / 2
			SET @pagenumber = @pagecount
			SET @SuffixFilter = RTRIM(@Suffixes)
			SET @JFilter = RTRIM(@JList)
		END
	END
	IF @joseqcount > 10
	BEGIN
