	, LEFT(JO02.SSN, 3) + '-' + LEFT(RIGHT(JO02.SSN, 6), 2) + '-' + RIGHT(JO02.SSN, 4) AS JO02SSN
	, RTRIM(JO02.FullAddress) AS JO02Address
