	WHERE (NMR.Created = 'Y' OR NMR.Created = 'I') AND NMRFT.FundingCardID <> '' AND NMR.Processed = 998 AND CAST(NMR.UserSessionXML AS VARCHAR(100)) <> '' AND (NMR.CreatedOn >= @StartDate AND NMR.CreatedOn <= @StopDate)) b ON 1=1
		FULL OUTER JOIN
