	DELETE FROM IMS.NewMemberSubAccountLink
	WHERE TrackingNum = @TrackingNum AND SubSequence NOT IN (SELECT Sequence
		FROM IMS.NewMemberSubAccount
		WHERE TrackingNum = @TrackingNum)

