									ON NMR.TrackingNum = NMRH.TrackingNum
								LEFT JOIN IMS.NewMemberWork NMRW
									ON NMR.TrackingNum = NMRW.TrackingNum					
			WHERE NOT EXISTS(SELECT TrackingNum FROM @ReviewQueue RQ Where RQ.TrackingNum = NMR.TrackingNum) AND
