							NMR.TNExpireDate
					FROM IMS.NewMemberRequest NMR 
							INNER JOIN 
							IMS.NewMemberManualApproval MA 
								ON NMR.TrackingNum = MA.TrackingNum 
									INNER JOIN IMS.ReviewQueueConfig RQC ON RQC.StatusCode = 'MR' AND RQC.ReasonCode = LTRIM(RTRIM(MA.ReasonCode))
