FETCH NEXT FROM curs INTO @docsubname
SET @newdocsub = 'Membership Application ' + @docsubname
IF @docsubname Is Null
BEGIN
	SET @newdocsub = 'Membership Application'
END
SET @count = 0
SET @docindex = 0
PRINT @@FETCH_STATUS
   WHILE @@FETCH_STATUS = 0
   BEGIN
	SET @count = @count + 1
	PRINT @count
      	IF @count = 1
	BEGIN
