	END
      	FETCH NEXT FROM curs INTO @docsubname
	PRINT @docsubname
	IF @@FETCH_STATUS = 0
	BEGIN
		SET @newdocsub = (RTRIM(@newdocsub) + ', ' + RTRIM(@docsubname))
		PRINT @newdocsub
		UPDATE NewMemberDoc SET docsubname = @newdocsub WHERE docindex = @docindex
	END
