	GROUP BY CONVERT(int, JO.Sequence)

OPEN curs2
FETCH NEXT FROM curs2 INTO @joseq
SET @count = 0
PRINT @@FETCH_STATUS
   WHILE @@FETCH_STATUS = 0
   BEGIN
      	IF (@count = 0 AND @DocIndex = 1) OR (@count = 3 AND @DocIndex = 2) OR (@count = 6 AND @DocIndex = 3)
