                     WHEN 'EMPLOYED' THEN [IMS].fnSSCU_GetQuestion(@TrackingNum, 'DDQ2-1')
                     WHEN 'UNEMPLOYED' THEN [IMS].fnSSCU_GetQuestion(@TrackingNum, 'DDQ2-1')
                     WHEN 'RETIRED' THEN [IMS].fnSSCU_GetQuestion(@TrackingNum, 'DDQ2-1')
