/*
 ==================================================================
  Function Name: dbo.admin_udfConcatAddress

  Description: 	Takes the address variables passed in and concats them 
				together with <br> in between lines and returns this as
				one string for display on a web page.

  Resources: 	none
  ------------------------------------------------------------------
  Accepts:		@address1 varchar(32),
				@address2 varchar(32), 
				@address3 varchar(32), 
				@city varchar(32),
				@state varchar(5), 
				@PostalCode varchar(11), 
				@country varchar(3)
		
  Returns:		@completeAddress
   ==================================================================
   Created:  	GNordstrom
   By:    		12/22/2005
  ------------------------------------------------------------------
  Modifications: 
===================================================================
*/
CREATE   FUNCTION  [dbo].[admin_udfConcatAddress]
		(@address1 varchar(32),
		@address2 varchar(32), 
		@address3 varchar(32), 
		@city varchar(32),
		@state varchar(5), 
		@PostalCode varchar(11), 
		@country varchar(3), 
		@phone varchar(32) = NULL)
RETURNS varchar(200)
AS
BEGIN
	DECLARE @completeAddress varchar(200)
	
	IF LEN(RTRIM(@address1)) > 0 
		Set @completeAddress = RTRIM(@address1) + '<BR>'
	IF LEN(RTRIM(@address2)) > 0 
		Set @completeAddress = @completeAddress + RTRIM(@address2) + '<BR>'
	IF LEN(RTRIM(@address3)) > 0 
		Set @completeAddress = @completeAddress + RTRIM(@address3) + '<BR>'
	
	Set @completeAddress = @completeAddress +  RTRIM(@city) + ', ' + RTRIM(@state) + 
		SPACE(2) + RTRIM(@PostalCode) + '<BR>' + RTRIM(@country) 

	IF @phone IS NOT NULL AND LEN(@Phone) > 0 
		Set @completeAddress = @completeAddress + '<BR>' + RTRIM(@phone)


	RETURN @completeAddress
END







