/*
 ==================================================================
  Function Name: dbo.admin_udfPayeeHasDependents

  Description:	Determines if the PayeeID passed in has any dependent 
				payment records.  There are five tables where there 
				could be dependent payment records.  Start checking 
				most likely table and stop checking tables as soon as  
				any dependent records are found because the total 
				dependent count is irrelevant.

  Resources: 	recurring_payment
				payment
				user_payee
				payee
				payment_history
  ------------------------------------------------------------------
  Accepts:		@PayeeID varchar(12)
		
  Returns:		@DepCount Int -- If 0 then no dependents.
								 If > 0 then there are dependents.								
   ==================================================================
   Created:  	GNordstrom
   By:    		12/28/2005
  ------------------------------------------------------------------
  Modifications: 
===================================================================
*/
CREATE     FUNCTION  [dbo].[admin_udfPayeeHasDependents]
		(@PayeeID varchar(12))
RETURNS Int 
AS
BEGIN
	
	DECLARE @DepCount int

	SELECT @DepCount = count(*) 
		FROM payment_history WITH (NOLOCK) 
		WHERE PayeeId = @PayeeId

	IF @DepCount = 0
	  BEGIN
		SELECT @DepCount = count(*) 
			FROM recurring_payment r WITH (NOLOCK) 
				INNER JOIN user_payee u WITH (NOLOCK) 
						ON r.PayeeListId = u.PayeeListId 
							and r.MemberId = u.MemberId 
			WHERE u.payeeId = @PayeeId

		IF @DepCount = 0
		  BEGIN
			SELECT @DepCount = count(*) 
				FROM payment p WITH (NOLOCK) 
					INNER JOIN user_payee u WITH (NOLOCK) 
							ON p.PayeeListId = u.PayeeListId 
							and p.MemberId = u.MemberId 
				WHERE u.PayeeId = @PayeeId

			IF @DepCount = 0
			  BEGIN
				SELECT @DepCount = count(*) 
					FROM payee WITH (NOLOCK) 
				WHERE PayeeAliasId = @PayeeId
				
				IF @DepCount = 0
				  BEGIN
					SELECT @DepCount = count(*) 
						FROM user_payee WITH (NOLOCK) 
						WHERE PayeeId = @PayeeId
				  END
			  END
		  END
	  END

	RETURN @DepCount

END





