/*
 ==================================================================
  Function:    admWRGGetActiveUsers

  Description: Obtains the memberid and last payment date of the 
                           active users for the date range passed in. 	Note  
                           this sp first determines if the dbcontains the new 
                           change history tables before deciding which sql stmt 
                           to execute.

  Resources: 	User_Payee_Change_History - if exists in the db
                           Payment_Change_History - if exists in the db
                           Recurring_Payment_Change_History - if exists in the db
                           Change_History
                           User_Payee
                           Payment
                           Payment_History
                           Recurring_Payment
  ------------------------------------------------------------------
  Accepts:     @Start datetime, 
                        @End   datetime
  
  Returns:     Table
   ==================================================================
   Created:    06/02/2008  	
   By:    	         GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE FUNCTION  [dbo].[admWRGGetActiveUsers]
                                       (@Start smalldatetime, 
                                       @End   smalldatetime) 
RETURNS @Result TABLE (MemberID varchar(32), LastPaymentDate smalldatetime) 
BEGIN         
   --If the database contains the new change history tables   
   --then execute the sql that includes those tables.
   IF EXISTS(SELECT [Name] FROM sys.tables WHERE [Name] like '%_Change_History')
   BEGIN
      INSERT INTO @Result 
         SELECT   a.MemberID, 
                  MAX(h.ProcessedDate) as LastPaymentDate   
            FROM (
               --counts user_payee records changed and cancelled
               SELECT DISTINCT MemberID 
                  FROM User_Payee_Change_History WITH (NOLOCK) 
                  WHERE CONVERT(varchar, ModifyDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts payments changed and cancelled
               SELECT DISTINCT MemberID
                  FROM Payment_Change_History WITH (NOLOCK)  
                  WHERE CONVERT(varchar, ModifyDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts recurring payments changed and cancelled
               SELECT DISTINCT MemberID 
                  FROM Recurring_Payment_Change_History WITH (NOLOCK)  
                  WHERE CONVERT(varchar, ModifyDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts changes to user_payee records in old change history table
               SELECT DISTINCT MemberID 
                  FROM Change_History ch WITH (NOLOCK) INNER JOIN User_Payee up WITH (NOLOCK) ON ch.PayeeListID = up.PayeeListID 
                  WHERE CONVERT(varchar, ch.LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
	                  AND ch.PayeeListID IS NOT NULL 
               UNION
               --counts changes to payment records in old change history table
               SELECT DISTINCT MemberID 
                  FROM Change_History ch WITH (NOLOCK) INNER JOIN Payment p WITH (NOLOCK) ON ch.PaymentID = p.PaymentID 
                  WHERE CONVERT(varchar, ch.LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
	                  AND ch.PaymentID IS NOT NULL 
               UNION
               --counts changes to payment records in old change history table not in payment table any longer 
               SELECT DISTINCT MemberID 
                  FROM Change_History ch WITH (NOLOCK) INNER JOIN Payment_History ph WITH (NOLOCK) ON ch.PaymentID = ph.PaymentID 
                  WHERE CONVERT(varchar, ch.LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
	                  AND ch.PaymentID IS NOT NULL 
               UNION
               --counts processed payments
               SELECT DISTINCT MemberID
                  FROM Payment_History WITH (NOLOCK)   
                  WHERE CONVERT(varchar, ProcessedDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts cancelled /stopped payments
               SELECT DISTINCT MemberID
                  FROM Payment_History WITH (NOLOCK)   
                  WHERE CONVERT(varchar, CancelledDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts failed payments
               SELECT DISTINCT MemberID
                  FROM Payment_History WITH (NOLOCK)   
                  WHERE CONVERT(varchar, FailedDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION 
               --counts new and chgd payments
               SELECT DISTINCT MemberID 
                  FROM Payment WITH (NOLOCK)   
                  WHERE CONVERT(varchar, LastUpdateDatetime, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION 
               --counts new and chgd recurring payments
               SELECT DISTINCT MemberID 
                  FROM Recurring_Payment WITH (NOLOCK)   
                  WHERE CONVERT(varchar, LastUpdateDatetime, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts new and chgd user_payee records
               SELECT DISTINCT MemberID 
                  FROM User_Payee WITH (NOLOCK)  
                  WHERE CONVERT(varchar, LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
            ) a LEFT JOIN Payment_History h ON a.MemberID = h.MemberID 
         GROUP BY a.MemberID 
         ORDER BY a.MemberID 
   END
   ELSE
   --If the database does NOT contain the new change history tables   
   --then execute the sql that doesn NOT include those tables.
   BEGIN
      INSERT INTO @Result 
         SELECT   a.MemberID, 
                  MAX(h.ProcessedDate) as LastPaymentDate    
            FROM (
               --counts changes to user_payee records in old change history table
               SELECT DISTINCT MemberID 
                  FROM Change_History ch WITH (NOLOCK) INNER JOIN User_Payee up WITH (NOLOCK) ON ch.PayeeListID = up.PayeeListID 
                  WHERE CONVERT(varchar, ch.LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
	                  AND ch.PayeeListID IS NOT NULL 
               UNION
               --counts changes to payment records in old change history table
               SELECT DISTINCT MemberID 
                  FROM Change_History ch WITH (NOLOCK) INNER JOIN Payment p WITH (NOLOCK) ON ch.PaymentID = p.PaymentID 
                  WHERE CONVERT(varchar, ch.LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
	                  AND ch.PaymentID IS NOT NULL 
               UNION
               --counts changes to payment records in old change history table not in payment table any longer 
               SELECT DISTINCT MemberID 
                  FROM Change_History ch WITH (NOLOCK) INNER JOIN Payment_History ph WITH (NOLOCK) ON ch.PaymentID = ph.PaymentID 
                  WHERE CONVERT(varchar, ch.LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
	                  AND ch.PaymentID IS NOT NULL 
               UNION
               --counts processed payments
               SELECT DISTINCT MemberID
                  FROM Payment_History WITH (NOLOCK)   
                  WHERE CONVERT(varchar, ProcessedDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts cancelled /stopped payments
               SELECT DISTINCT MemberID
                  FROM Payment_History WITH (NOLOCK)   
                  WHERE CONVERT(varchar, CancelledDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts failed payments
               SELECT DISTINCT MemberID
                  FROM Payment_History WITH (NOLOCK)   
                  WHERE CONVERT(varchar, FailedDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION 
               --counts new and chgd payments
               SELECT DISTINCT MemberID 
                  FROM Payment WITH (NOLOCK)   
                  WHERE CONVERT(varchar, LastUpdateDatetime, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION 
               --counts new  and chgd recurring payments
               SELECT DISTINCT MemberID 
                  FROM Recurring_Payment WITH (NOLOCK)   
                  WHERE CONVERT(varchar, LastUpdateDatetime, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
               UNION
               --counts new and chgd user_payee records
               SELECT DISTINCT MemberID 
                  FROM User_Payee WITH (NOLOCK)  
                  WHERE CONVERT(varchar, LastUpdatedOn, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112)       
            ) a  LEFT JOIN Payment_History h ON a.MemberID = h.MemberID 
         GROUP BY a.MemberID 
         ORDER BY a.MemberID 
   END
   
   RETURN 
END

