/*
 ==================================================================
  Function Name:  BPG_udfElapsedTimeFromSeconds

  Description:    Calculates the hours, minutes, and seconds that are
                  in the variable passed in.

  Resources: 	none
  ------------------------------------------------------------------
  Accepts:		@Seconds Int   -- The amount of elapsed time in seconds.
		
  Returns:		@ElapsedTime   varchar(12) -- 'hh:mm:ss'
   ==================================================================
   Created:  	08/23/2006
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
===================================================================
*/
CREATE FUNCTION [dbo].[BPG_udfElapsedTimeFromSeconds]  
                 (@Seconds Int) 
   RETURNS varchar(12)
AS 

BEGIN

DECLARE @ElapsedTime varchar(12) 
DECLARE @Mins Int
DECLARE @Hrs  Int

IF @Seconds < 60 
    SET @ElapsedTime = '00:00:' + (CASE LEN(@Seconds) WHEN 2 THEN CONVERT(Varchar, @Seconds) ELSE ('0' + CONVERT(Varchar, @Seconds)) END)  
ELSE
   BEGIN
      SET @Mins = (@Seconds / 60)
      SET @Seconds = (@Seconds - (@Mins * 60))
      
      IF @Mins < 60 
         SET @ElapsedTime = '00:' + (CASE LEN(@Mins) WHEN 2 THEN CONVERT(Varchar, @Mins) ELSE ('0' + CONVERT(Varchar, @Mins)) END) 
                     + ':' + (CASE LEN(@Seconds) WHEN 2 THEN CONVERT(Varchar, @Seconds) ELSE ('0' + CONVERT(Varchar, @Seconds)) END)   
      ELSE
         BEGIN
            SET @Hrs = (@Mins /60) 
            SET @Mins = (@Mins - (@Hrs * 60))
            
            SET @ElapsedTime = (CASE LEN(@Hrs) WHEN 2 THEN CONVERT(Varchar, @Hrs) ELSE ('0' + CONVERT(Varchar, @Hrs)) END) 
                        + ':' + (CASE LEN(@Mins) WHEN 2 THEN CONVERT(Varchar, @Mins) ELSE ('0' + CONVERT(Varchar, @Mins)) END) 
                        + ':' + (CASE LEN(@Seconds) WHEN 2 THEN CONVERT(Varchar, @Seconds) ELSE ('0' + CONVERT(Varchar, @Seconds)) END)   
         END
   END
   
   
RETURN @ElapsedTime 
END
