-- Author:		Helen Liou
-- Create date: 2/20/2007
-- Description:	Returns the detail record format for the on-us file
-- =============================================
CREATE FUNCTION [dbo].[OnUsDetail] (
@Account varchar(32),
@AccountLength int,
@SuffixLength int, 
@Amount int,
@WillProcessDate datetime)

RETURNS varchar(1000)
AS
BEGIN
	-- Declare the return variable here
	DECLARE 
		@AccountPrefix varchar(32),
		@AccountSuffix	varchar(4),
		@Return varchar(1000)
	
	DECLARE 
		@rc int

-- Remove blanks
	SET @Account = REPLACE(@Account, ' ', '')

--  Get the account suffix

	IF CHARINDEX('L',@Account) = 0
	BEGIN
		IF CHARINDEX('-', @Account) = 0
			SET @AccountSuffix = REPLICATE('?',@SuffixLength)
		ELSE
		BEGIN
			SET @AccountSuffix =  SUBSTRING(@Account, charindex('-',@Account) + 1, @SuffixLength )
			IF LEN(@AccountSuffix) < @SuffixLength
				SET @AccountSuffix = REPLICATE('?',@SuffixLength- Len(@AccountSuffix))
		END
	END
	ELSE
	BEGIN
		SET @AccountSuffix = SUBSTRING(@Account, charindex('L',@Account) + 1, @SuffixLength )
		IF LEN(@AccountSuffix) < @SuffixLength
			SET @AccountSuffix = REPLICATE('?',@SuffixLength- Len(@AccountSuffix))
	END

	IF CHARINDEX('-', @Account) = 0  -- Check to see if there is an 'L' to key off of
	BEGIN
		IF CHARINDEX('L', @Account) = 0	
			SET @AccountPrefix = @Account
		ELSE
			SET @AccountPrefix = SUBSTRING(@Account, 1, charindex('L',@Account) - 1)
	END
	ELSE
		SET @AccountPrefix = SUBSTRING(@Account, 1, charindex('-',@Account) - 1)


--  Get the Account Suffix
	IF @AccountLength > 11
		SET @Return = 'VOUC' + REPLACE(STR(@AccountPrefix, @AccountLength-@SuffixLength), ' ','0') + 
			 SUBSTRING(@AccountSuffix,1,@SuffixLength) + STR(CONVERT(char(8), @Amount) ,8) + 
			'GL' + SUBSTRING(CONVERT(char(10), @Willprocessdate, 112), 3, 6) + 'NOLPMT##    ABILLPAY*     '
	ELSE
		SET @Return = 'VOUC' + REPLACE(STR(@AccountPrefix, @AccountLength-@SuffixLength), ' ','0') + 
			SUBSTRING(@AccountSuffix,1,@SuffixLength) + STR(CONVERT(char(8), @Amount) ,10) + 
			'GL' + SUBSTRING(CONVERT(char(10), @Willprocessdate, 112), 3, 6) + 'NOLPMT##    ABILLPAY*     '
	
	RETURN @Return
	
END

