CREATE FUNCTION  [dbo].[admWRGGetOnUsCounts]
                                      (@Start smalldatetime, 
                                       @End   smalldatetime) 
RETURNS TABLE 
AS  
RETURN SELECT pr.ProviderID, 
             COUNT(h.PaymentID) OnUsCnt, 
             SUM(CAST(h.Amount as BigInt))  OnUsAmt            
         FROM Payment_History h WITH (NOLOCK) 
            INNER JOIN User_Payee u WITH (NOLOCK) ON h.PayeeListID = u.PayeeListID 
            INNER JOIN Payee p WITH (NOLOCK) ON u.PayeeID = p.PayeeID  
            INNER JOIN Provider pr WITH (NOLOCK) ON p.ProviderID = pr.ProviderID 
         WHERE h.StatusCode IN (99, 101, 102, 1036) 
            AND pr.Active = 'Y'  
            AND pr.ProviderID IN (101, 200, 201)
            AND CONVERT(varchar, h.ProcessedDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112) 
         GROUP BY pr.ProviderID  

