CREATE VIEW [dbo].[wc_ListCombineChanges]
AS
SELECT H.*, PC.Name AS OldName, PC.Address1 AS OldAddress1, 
    PC.Address2 AS OldAddress2, PC.City AS OldCity, 
    PC.State AS OldState, PC.PostalCode AS OldPostalCode, 
    PC.Phone AS OldPhone, P.*
