CREATE PROCEDURE [dbo].[AddNewBiller]
AS

SET NOCOUNT ON

DECLARE @Message varchar(255),@PCDirPath varchar(255), @FileName varchar(255), 
	     @ListFile varchar(255), @SQL varchar(1000), @PCFormat varchar(255),
	     @PCFile varchar(255),  @Temp varchar(255), @BlrId varchar(10), @frequency int, @day int,@PayeeName varchar(32),@PayeeID varchar(10),
	     @duedate datetime, @usersAccount varchar(32), @amount int, @Addr1 varchar(32), @Addr2 varchar(32), @City varchar(32), @State varchar(2), @Zip varchar(11), 
	     @payeeListID varchar(12), @NameOnAccount varchar(60), @suffix char(2), @newaccount varchar(15), @StatusCode char(1), @nduedate datetime
DECLARE @rc int

DECLARE pc CURSOR FOR
	SELECT Distinct blrid
	FROM WRGBPS..Biller Order by Blrid asc
OPEN pc
FETCH NEXT FROM pc INTO @Blrid
	WHILE  @@FETCH_STATUS= 0
	BEGIN

		DECLARE addr CURSOR FOR 
			SELECT DisplayBillerName, ISNULL(DisplayAddr1,' '), ISNULL(DisplayAddr2,' '), ISNULL(City,' '), ISNULL(State,' '), ISNULL(DisplayZip,' ')
				FROM WRGBPS..Biller B Inner Join WRGBPS..Biller_Address BA on B.Blrid = BA.Blrid  WHERE B. blrID = @BlrID
		OPEN addr
		FETCH NEXT FROM addr INTO @Payeename, @addr1, @addr2, @City, @State, @Zip
		WHILE @@FETCH_STATUS = 0
		BEGIN

			EXEC @rc = BillerAdd '000000',@PayeeName, @Addr1, @Addr2, '', @City, @State, @Zip, @Blrid
			IF @rc <> 0
			BEGIN
				print 'Error adding a biller : ' + @BlrID
				GOTO NextPayee
			END
NextPayee:
			FETCH NEXT FROM addr INTO @Payeename, @addr1, @addr2, @City, @State, @Zip
		END

CLOSE addr
DEALLOCATE addr

	FETCH NEXT FROM pc INTO @BlrID
	
	END

AcceptFile:
CLOSE pc
DEALLOCATE pc

IF EXISTS (SELECT * FROM SYSOBJECTS WHERE id = object_id(N'[dbo].[TEMP_CONFIRMATION]') and OBJECTPROPERTY(id, N'IsUserTable') = 1)
	DROP TABLE [dbo].[TEMP_CONFIRMATION]

spExit:
RETURN 0
GO
