/*
 ==================================================================
  Stored Procedure Name: dbo.admin_CancelPayment

  Description: 	If the payment to be cancelled exists in the payment 
			         table it copies the record to the payment_history table 
			         and then deletes the record in the payment table and 
			         executes the admin_AddChangeHistory procedure.  
			         If the payment to be cancelled does not exist in the 
			         payment table then looks if the payment is in the 
			         payment_history table.

  Resources: 	Payment
				   Payment_History
				   User_Payee
				   Payment_Change_History
  ------------------------------------------------------------------
  Accepts:		@member_id	char(32),
				   @payment_id	char(32),
				   @user_type	tinyint,
				   @change_reason	char(255),	
				   @canceldate datetime = NULL, 
				   @LastUpdatedBy varchar(32)	

  Returns:		Status,	(0 if successful, 1 if no match, -1 if error occurs, 
						StatusCode from payment_history table if the payment already in that table)
				   StatusDate (only if the payment already exists in the payment_history table)
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	12/16/2005	GNordstrom	Copied PaymentCancel to this new proc name.
	12/21/2005	GNordstrom	Added @LastUpdatedBy variable.
	12/30/2005	GNordstrom	Changed no match status to return 1 instead of 9, 
							      so that the code returned is the same as those codes
							      returned for admin_CancelRecurPayment.
	06/13/2006  	GNordstrom  	Added the OUTPUT clause to the DELETE statement and
	                	        removed the exec admin_InsertChangeHistory.
   	01/26/2007  	GNordstrom  	Added inserting the Payment.DueDate field into the 
                        	   	Payment_History.DueDate field and inserting a blank 
 	                          	into the Payment_History.MemberEmail field.
   	09/04/2007  	GNordstrom  	Added inserting the Payment.TrsfSource field into the 
                        	   	Payment_History table and the payment_change_history table.
	4/28/2013	HLiou		Added Delivery Date to track as well.
    	6/1/2014	HLiou		Added ProviderID to history table

==================================================================
*/
CREATE     PROCEDURE [dbo].[admin_CancelPayment]
					@member_id	char(32),
					@payment_id	char(32),
					@user_type	tinyint,	-- Required parameter, the user type that caused the change.
					@change_reason	char(255),	-- Required parameter, the reason the change occurred.
					@canceldate datetime = NULL,	-- optional
					@LastUpdatedBy  varchar(32)
AS


BEGIN TRANSACTION
	--
	-- Step 1: Verify that the specified payment is in the PAYMENT table.
	--	   If not, see if it's listed in the PAYMENT_HISTORY table.
	--
	DECLARE @rows int
	DECLARE @payeelistid	char(12)
	DECLARE @date_to_use	datetime
	
	SELECT @rows = Count(*) 
		FROM Payment 
		WHERE MemberID=@member_id 
				And PaymentID=@payment_id
	IF (@@ERROR <> 0) GOTO on_error
	IF (@rows = 1)
		BEGIN
		  SELECT @payeelistid=PayeeListID 
			FROM Payment  with (nolock) 
			WHERE MemberID=@member_id 
					And PaymentID=@payment_id
		  IF (@@ERROR <> 0) GOTO on_error
		  GOTO transfer_record
		END

	-- Not found in PAYMENT, so check PAYMENT_HISTORY
	SELECT @rows = Count(*) 
		FROM Payment_History  with (nolock) 
		WHERE MemberID=@member_id 
				And PaymentID=@payment_id
	IF (@@ERROR <> 0) GOTO on_error
	IF (@rows = 0) GOTO no_match
	
	-- Found in PAYMENT_HISTORY, so get the status
	DECLARE @status int
	DECLARE @procdt smalldatetime, @faildt smalldatetime, @cancdt smalldatetime
	SELECT 	@status=StatusCode, 
			@procdt=ProcessedDate, 
			@faildt=FailedDate, 
			@cancdt=CancelledDate,
			@payeelistid=PayeeListID
		FROM Payment_History  with (nolock) 
		WHERE MemberID=@member_id 
				And PaymentID=@payment_id
	IF (@@ERROR <> 0) GOTO on_error
	SELECT 	"Status"=@status, 
			"StatusDate"=@procdt
	COMMIT TRANSACTION
	RETURN (0)
	
transfer_record:
	--
	-- Step 2: Copy the information from the PAYMENT record to a new PAYMENT_HISTORY record.
	-- NOTES:  1: A StatusCode of '103' indicates a cancellation.
	--	   2: The order of values in the INSERT statement must match the PAYMENT_HISTORY
	--	      (not PAYMENT) table definition EXACTLY!!
	--
	-- First we need to get PayeeId and UsersAccountAtPayee, and NameOnAccount from USER_PAYEE table
	DECLARE @payeeid char(12), @useracct char(32), @nameonacct char(60), @ProviderID int
	SELECT @payeeid = NULL
	SELECT 	@payeeid = U.PayeeID, 
			@useracct = UsersAccountAtPayee, 
			@nameonacct = NameOnAccount 
		FROM User_Payee U with (nolock)
		INNER JOIN Payee P with (nolock) ON	U.PayeeID = P.PayeeID 
		WHERE U.MemberID=@member_id 
				And PayeeListID=@payeelistid
	IF (@@ERROR <> 0) GOTO on_error
	-- Use canceldate parameter if exists, otherwise default to current time
	IF (@canceldate IS NULL) 
		BEGIN 
			SELECT @date_to_use = GetDate()
		END
	ELSE 
		BEGIN
	    		SELECT @date_to_use = @canceldate
		END

	INSERT INTO Payment_History 
		SELECT	PaymentID, 
			PayeeListID, 
			Account, 
			Amount, 
			'',
			WillProcessDate, 
			@date_to_use, 
			NULL, 
			@date_to_use, 
			RecPaymentID,
			103, 
			Memo, 
			BillReference,
			MemberID, 
			@payeeid,	
			0,
			'', 
			'', 
			'', 
			'', 
			'', 
			'', 
			'', 
			'', 
			'', 
			SourceApplication, 
			@useracct, 
			@nameonacct, 
			'',
			'',
			'', 
			'', 
			DueDate, 
			TrsfSource,
			EntryDate, 
			DeliveryDate, 
			@ProviderID 
		FROM Payment 
		WHERE MemberID=@member_id 
   		And PaymentID=@payment_id
	if (@@ERROR <> 0) GOTO on_error
	--
	-- Step 3: Delete the PAYMENT record.
	--
	DELETE FROM Payment 
		OUTPUT DELETED.PaymentID, DELETED.PayeeListID, DELETED.Account, DELETED.Amount, 
		      DELETED.WillProcessDate, DELETED.RecPaymentID, DELETED.StatusCode, DELETED.Memo, 
		      DELETED.BillReference, DELETED.LastUpdateDateTime, DELETED.MemberID, 
		      DELETED.SourceApplication, DELETED.EntryDate, DELETED.DueDate, @user_type, 
		      @change_reason, 'D', GETDATE(), @LastUpdatedBy, DELETED.TrsfSource, DELETED.DeliveryDate  
		   INTO Payment_Change_History (PaymentID, PayeeListID, Account, Amount, WillProcessDate, 
		      RecPaymentID, StatusCode, Memo, BillReference, LastUpdateDateTime, MemberID, 
		      SourceApplication, EntryDate, DueDate, User_Type, Reason, ModifyType, ModifyDate, 
		      ModifiedBy, TrsfSource, DeliveryDate)  
		WHERE MemberID=@member_id 
				And PaymentID=@payment_id 
		
	if (@@ERROR <> 0) GOTO on_error
	
COMMIT TRANSACTION
SELECT "Status"=0
RETURN(0)

no_match:
	ROLLBACK TRANSACTION
	SELECT "Status"=1
	RETURN (1)
on_error:
	ROLLBACK TRANSACTION
	SELECT "Status"=-1
	RETURN(-1)
