/*
 ==================================================================
  Stored Procedure Name: dbo.admin_CancelRecurPayment

  Description: 	

  Resources: 	Recurring_Payment
				            User_Payee
				            Payment_History
				            Recurring_Payment_Change_History
     ------------------------------------------------------------------
  Accepts:		@recpmtid		char(10),
				         @user_type		tinyint,
				         @change_reason	char(255),
				         @canceldate datetime = NULL, 
				         @LastUpdatedBy  varchar(32)

  Returns:		Status	(0 if successful, 1 if no match, -1 if error occurs)
   ==================================================================
   Created:  	?
   By:   		? 			
  ------------------------------------------------------------------
  Modifications: 
	12/19/2005	GNordstrom	Copied RecPmtCancel to this new proc name.
	12/21/2005	GNordstrom	Added @LastUpdatedBy variable.
	06/14/2006  	GNordstrom  	Added the OUTPUT clause to the UPDATE statement and
	                                     removed the "exec admin_InsertChangeHistory".
	                                     Also added a transaction to the processing.
	01/26/2007  	GNordstrom  	Added updating of NextDueDate field in the 
	                                     Recurring_Payment_Change_History table.  Added  
	                                     inserting Recurring_payment.NextDueDate field into 
	                                     the Payment_History.DueDate field and inserting a 
	                                     blank into the Payment_History.MemberEmail field.
   	09/04/2007  	GNordstrom  	Added inserting the Recurring_Payment.TrsfSource field into the 
  	                                     Payment_History table and the recurring_payment_change_history table.
   	09/18/2007 	 GNordstrom  	Modified default for @canceldate because it was not
                                             always working correctly as previously coded.
   	06/18/2009  	GNordstrom  	Added new field, ProcessDay, to OUTPUT statement. 
   	5/20/2012   	HLiou	   	Added EntryDate to payment history table
   	4/28/2013   	HLiou	   	Added DeliveryDate to Recurring and history tables.
   	6/1/2014    	HLiou	   	Added ProviderID to history table

==================================================================
*/
CREATE    PROCEDURE [dbo].[admin_CancelRecurPayment]
					@recpmtid		char(10),
					@user_type		tinyint,
					@change_reason	char(255),
					@canceldate datetime = NULL, 
					@LastUpdatedBy  varchar(32)
AS

SET NOCOUNT ON

--
-- First make sure the specified recurring payment exists in the database.
--
DECLARE @rows int
DECLARE @member_id char(32)
DECLARE @payeelistid char(12)
DECLARE @date_to_use datetime
DECLARE @willProcessDate	smalldatetime
DECLARE @amount		int
--
-- Use canceldate parameter if exists, otherwise default to current time
--
IF (@canceldate IS NOT NULL AND LEN(@canceldate) > 0) 
	BEGIN 
		SELECT @date_to_use = @canceldate
	END
ELSE 
	BEGIN
		SELECT @date_to_use = GetDate()
	END
  
SELECT @rows = Count(*) 
	FROM Recurring_Payment  with (nolock) 
	WHERE RecPaymentId = @recpmtid

IF (@@ERROR <> 0) GOTO on_error
IF (@rows < 1) GOTO no_match

SELECT 	@member_id = MemberID, 
		@payeelistid = PayeeListID, 
		@WillProcessDate=NextProcessDate,  
		@amount =Amount
	FROM Recurring_Payment  with (nolock) 
	WHERE RecPaymentId = @recpmtid
	
BEGIN TRANSACTION

   UPDATE Recurring_Payment 
	   SET Active = 'N' 
	   OUTPUT DELETED.RecPaymentID, DELETED.PayeeListID, DELETED.MemberID, 
	         DELETED.Account, DELETED.Active, DELETED.Amount, DELETED.InitialAmount, 
	         DELETED.FinalAmount, DELETED.LastProcessedDate, DELETED.InitialDueDate, 
	         DELETED.NumPayments, DELETED.Frequency, DELETED.LastUpdateDateTime, 
	         DELETED.SourceApplication, DELETED.EntryDate, DELETED.[Type], 
	         DELETED.NextProcessDate, DELETED.NextDueDate, DELETED.PaymentsProcessed, DELETED.memo, 
	         @user_type, @change_reason, 'U', GetDate(), @LastUpdatedBy, DELETED.TrsfSource, DELETED.ProcessDay,
			 DELETED.DeliveryDate   
	      INTO Recurring_Payment_Change_History 
	            (RecPaymentID, PayeeListID, MemberID, Account, Active, Amount, InitialAmount, 
	            FinalAmount, LastProcessedDate, InitialDueDate, NumPayments, Frequency, 
	            LastUpdateDateTime, SourceApplication, EntryDate, [Type], NextProcessDate, NextDueDate, 
	            PaymentsProcessed, memo, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy, TrsfSource, ProcessDay,
				DeliveryDate)      
	   WHERE RecPaymentId = @recpmtid 
	
   --
   -- 04May1999: Add Cancel record to Payment History as per request
   --
   --
   -- Step 2: Copy the information from the PAYMENT record to a new PAYMENT_HISTORY record.
   -- NOTES:  1: A StatusCode of '103' indicates a cancellation.
   --	   2: The order of values in the INSERT statement must match the PAYMENT_HISTORY
   --	      (not PAYMENT) table definition EXACTLY!!
   --
   -- First we need to get PayeeId and UsersAccountAtPayee, and NameOnAccount from USER_PAYEE table
   DECLARE @payeeid		char(12)
   DECLARE @useracct		char(32)
   DECLARE @nameonacct		char(60)
   DECLARE @count		int
   DECLARE @uniquepmtid 		char(10)
   DECLARE @rc			int
   DECLARE @ProviderID	int

   SELECT @payeeid = NULL
   SELECT  @payeeid = U.PayeeID, 
		   @useracct = UsersAccountAtPayee, 
		   @nameonacct = NameOnAccount, 
		   @ProviderID = ProviderID
	   FROM User_Payee U with (nolock)
			INNER JOIN Payee P with (nolock) ON U.PayeeID = P.PayeeID
	   WHERE U.MemberID=@member_id 
		   AND PayeeListID=@payeelistid

   IF (@@ERROR <> 0) GOTO on_error
   -- Calculate next willProcessDate
   ---  EXEC NextPmtInfoOut @RecPaymentID = @recpmtid, @AmountOut = @amount OUTPUT, @NextPaymentDateOut = @willprocessdate OUTPUT, @PaymentNumberOut = @count OUTPUT

   -- Insert record into Payment_History
   -- Get new payment id
   EXEC @rc = GetID @id_type='PaymentID', @id=@uniquepmtid OUTPUT	--KKS 17Sep1999 - ID Dispenser replacement

   IF @rc < 0 GOTO on_error

   INSERT INTO Payment_History 
	   SELECT 	@uniquepmtid, 
			   PayeeListID, 
			   Account, 
			   @amount, 
			   '',
			   @willProcessDate, 
			   @date_to_use, 
			   NULL, 
			   @date_to_use, 
			   @recpmtid,
			   103, 
			   '', 
			   '',
			   MemberID, 
			   @payeeid, 0,
			   '', 
			   '', 
			   '', 
			   '', 
			   '', 
			   '', 
			   '', 
			   '', 
			   '', 
			   SourceApplication, 
			   @useracct, 
			   @nameonacct, 
			   '' ,
			   '',
			   '', 
			   '', 
			   NextDueDate, 
			   TrsfSource ,
			   EntryDate,
			   DeliveryDate,
			   @ProviderID
	   FROM Recurring_Payment 
	   WHERE MemberID=@member_id 
		   And RecPaymentID =  @recpmtid
   
   if (@@ERROR <> 0) GOTO on_error
		   
COMMIT TRANSACTION
SELECT "Status"=0
RETURN (0)

no_match:
	ROLLBACK TRANSACTION
	SELECT "Status"=1
	RETURN (1)
on_error:
	ROLLBACK TRANSACTION
	SELECT "Status"=-1
	RETURN (-1)

SET NOCOUNT OFF
GO
