/*
 ==================================================================
  Stored Procedure Name: dbo.admin_CombinePayees

  Description: 	Combines payee ids by replacing the payee ids to be 
				      combined to the master payee id in the user_payee
				      and payment_history tables.  And then deleting those
				      ids from the payee table.

  Resources: 	Payee
				   User_Payee
				   Payment_History
				   wc_NickNames
				   Payee_Change_History
				   User_Payee_Change_History 
  ------------------------------------------------------------------
  Accepts:		@MasterID varchar(12),
				   @CombineIDs varchar(280), 
				   @LastUpdatedBy varchar(32)

  Returns:		none
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	01/23/06	GNordstrom	Copied wc_CombinePayee to this new proc name.
	01/23/06	GNordstrom	Modified to take a comma delimited string for
							   the @CombineIDs variable.
	06/14/06 GNordstrom  Added output clause to the update User_Payee 
	                     statement and the delete Payee statment.
	12/12/06 GNordstrom  Added @LastUpdatedBy parameter.
==================================================================
*/
CREATE  PROCEDURE [dbo].[admin_CombinePayees]
					@MasterID varchar(12),
					@CombineIDs varchar(280),
					@LastUpdatedBy varchar(32)  
AS

SET NOCOUNT ON

DECLARE @intChar smallint
DECLARE @tblTemp TABLE (payeeID varchar(12))
DECLARE @MasterName varchar(40)
DECLARE @tblCombine TABLE (payeeID varchar(12), payeeName varchar(40))

WHILE Len(@CombineIDs) > 0 
	BEGIN
		INSERT INTO @tblTemp VALUES (SUBSTRING(@CombineIDs, 1, 12))
		SET @intChar = CHARINDEX(',', RTRIM(LTRIM(@CombineIDs)), 1)
	
		IF (@intChar > 0)
			SET @CombineIDs = ltrim(SUBSTRING(@CombineIDs, (@intChar + 1), len(@CombineIDs)))
		ELSE 
			BREAK
	END

SELECT @MasterName = Name 
	FROM Payee WITH (NOLOCK)  
	WHERE PayeeID = @MasterID

INSERT INTO @tblCombine 
	SELECT PayeeID, Name 
		FROM Payee WITH (NOLOCK) 
		WHERE PayeeID IN (SELECT payeeID FROM @tblTemp) 

BEGIN TRANSACTION
BEGIN
	--wc_NickNames
	INSERT INTO wc_NickNames 
		SELECT 	u.PayeeListID, 
				t.payeeName,
				u.MemberID 
			FROM USER_PAYEE u INNER JOIN @tblCombine t ON u.PayeeID = t.payeeID 
			WHERE t.payeeName <> @MasterName 
				AND u.PayeeListId NOT IN (SELECT PayeeListId FROM wc_NickNames)

	--USER_PAYEE
	UPDATE USER_PAYEE SET PayeeID = @MasterID  
	   OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, DELETED.PayeeID, INSERTED.CreatedOn, 
	            INSERTED.LastUpdatedOn, INSERTED.UsersAccountAtPayee, INSERTED.NameOnAccount, 
	            INSERTED.Active, INSERTED.MinDaystoPay, INSERTED.PaymentMethod, 
	            1, 'Payee Combined With ' + + @MasterID, 'U', GETDATE(), @LastUpdatedBy   
	      INTO User_Payee_Change_History (PayeeListID, MemberID, PayeeID, CreatedOn, 
	            LastUpdatedOn, UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, 
	            PaymentMethod, User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
	WHERE PayeeID IN (SELECT payeeID FROM @tblTemp)

	IF @@Error = 0 
		BEGIN
	
			--PAYMENT_HISTORY
			UPDATE PAYMENT_HISTORY SET PayeeID = @MasterID  
			WHERE PayeeID IN (SELECT payeeID FROM @tblTemp)	
			
			IF @@Error = 0 
				BEGIN
					--PAYEE
					DELETE FROM PAYEE  
		            OUTPUT DELETED.PayeeID, DELETED.PayeeAliasID, DELETED.ProviderID, DELETED.[Name], 
		                   DELETED.Address1, DELETED.Address2, DELETED.Address3, DELETED.City, 
		                   DELETED.State, DELETED.PostalCode, DELETED.Country, DELETED.Phone, 
		                   DELETED.Scope, DELETED.MinDaysToPay, DELETED.LastUpdatedOn, 
		                   DELETED.LastUpdatedBy, DELETED.BankID, DELETED.BranchID, DELETED.AccountID, 
		                   DELETED.AccountType, DELETED.PaymentMethod, DELETED.AccountKey, 
		                   DELETED.EntryDate, DELETED.PayeeType, DELETED.BillerKey, DELETED.AddressKey, 
		                   1, 'Payee Combined With ' + @MasterID, @MasterID, 'D', GETDATE(), @LastUpdatedBy 
		               INTO Payee_Change_History 
                        (PayeeID, PayeeAliasID, ProviderID, [Name], Address1, Address2, 
                        Address3, City, State, PostalCode, Country, Phone, Scope, MinDaysToPay, 
                        LastUpdatedOn, LastUpdatedBy, BankID, BranchID, AccountID, AccountType, 
                        PaymentMethod, AccountKey, EntryDate, PayeeType, BillerKey, AddressKey,	
                        User_Type, Reason, CombinedInto, ModifyType, ModifyDate, ModifiedBy) 
					WHERE PayeeID IN (SELECT payeeID FROM @tblTemp)	

					IF @@Error = 0 
						COMMIT TRANSACTION
					ELSE
						ROLLBACK TRANSACTION
				END
			ELSE
				ROLLBACK TRANSACTION
		END
	ELSE
		ROLLBACK TRANSACTION
END



SET NOCOUNT OFF

GO
