/*
 ==================================================================
  Stored Procedure Name: dbo.admin_CombinePayeesReverse

  Description: 	Reverses a combined payee by 
  replacing the payee ids to be 
				      combined to the master payee id in the user_payee
				      and payment_history tables.  And then deleting those
				      ids from the payee table.

  Resources: 	Payee
				   User_Payee
				   Payment_History
				   Payee_Change_History
				   User_Payee_Change_History 
  ------------------------------------------------------------------
  Accepts:		@PayeeID varchar(12) 

  Returns:		none
   ==================================================================
   Created:  	12/15/2006
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE  PROCEDURE [dbo].[admin_CombinePayeesReverse]
					      @PayeeID varchar(12)
AS


BEGIN TRANSACTION
BEGIN TRY
   --PAYEE
   INSERT INTO PAYEE  
      SELECT   PayeeID, 
               PayeeAliasID, 
               ProviderID, 
               [Name], 
               Address1, 
               Address2, 
               Address3, 
               City, 
               State, 
               PostalCode, 
               Country, 
               Phone, 
               Scope, 
               MinDaysToPay, 
               LastUpdatedOn, 
               LastUpdatedBy, 
               BankID, 
               BranchID, 
               AccountID, 
               AccountType, 
               PaymentMethod, 
               AccountKey, 
               EntryDate, 
               PayeeType, 
               BillerKey, 
               AddressKey 	
         FROM Payee_Change_History 
         WHERE PayeeID = @PayeeID

   --USER_PAYEE
   UPDATE USER_PAYEE SET PayeeID = @PayeeID  
      WHERE PayeeListID IN (SELECT PayeeListID 
                              FROM User_Payee_Change_History 
                              WHERE PayeeID = @PayeeID)

   --PAYMENT_HISTORY
   UPDATE PAYMENT_HISTORY SET PayeeID = @PayeeID   
   WHERE PayeeListID IN (SELECT PayeeListID 
                           FROM User_Payee_Change_History 
                           WHERE PayeeID = @PayeeID)
                              
    --Payee_Change_History
    DELETE FROM Payee_Change_History 
      WHERE PayeeID = @PayeeID    
      
    --User_Payee_Change_History
    DELETE FROM User_Payee_Change_History 
      WHERE PayeeID = @PayeeID  
                              
	COMMIT TRANSACTION
END TRY 

BEGIN CATCH
   IF (@@Trancount > 0) OR ((XACT_STATE()) = -1)
      ROLLBACK TRANSACTION
   
   DECLARE @ErrorMessage NVARCHAR(4000);
   DECLARE @ErrorSeverity INT;
   DECLARE @ErrorState INT;
   
   SELECT  @ErrorMessage = ERROR_MESSAGE(),
           @ErrorSeverity = ERROR_SEVERITY(),
           @ErrorState = ERROR_STATE();
   
   RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)

END CATCH

