/****** Object:  StoredProcedure [dbo].[admin_CopyUserPayees]    Script Date: 1/4/2025 10:12:55 AM ******/
SET ANSI_NULLS OFF
GO
SET QUOTED_IDENTIFIER OFF
GO
/*
 ==================================================================
  SP Name:     admin_CopyUserPayees

  Description:	Copies all active user payee records for the from 
               member id to the to member id. 	

  Resources: 	User_Payee
               Payee
               wc_NickNames
               User_Payee_Change_History
               wc_NickNames_Change_History 
  ------------------------------------------------------------------
  Accepts:		@From varchar(32), 
               @To varchar(32), 
               @UpdatedBy varchar(32)

  Returns:		First Recordset:
                  t.FromListID,  
                  t.ToListID, 
                  t.PayeeID, 
                  p.Name as PayeeName, 
                  t.UsersAcct, 
                  t.NameOnAcct, 
                  t.Active, 
                  t.MinDays, 
                  t.Method, 
                  t.Nickname, 
                  CASE WHEN t.ToListID IS NULL THEN 'No' 
                     ELSE 'Yes' 
                  END as Copied 
               Second Recordset:
                  t.FromListID,  
                  t.ToListID, 
                  t.PayeeID, 
                  p.Name as PayeeName, 
                  t.UsersAcct, 
                  t.NameOnAcct, 
                  t.Active, 
                  t.MinDays, 
                  t.Method, 
                  t.Nickname                
   ==================================================================
   Created:  	07/14/2008
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE   PROCEDURE [dbo].[admin_CopyUserPayees] 
				         @From varchar(32), 
                     @To varchar(32), 
                     @UpdatedBy varchar(32)
AS

SET NOCOUNT ON
DECLARE  @SPName        varchar(255), 
         @FromListID    varchar(12), 
         @NewListID     varchar(12), 
         @PayeeID       varchar(12), 
         @UsersAcct     varchar(32), 
         @NameOnAcct    varchar(60),  
         @Active        varchar(1), 
         @MinDays       int, 
         @Method        varchar(1), 
         @Nickname      varchar(50), 
         @Count         smallint, 
         @CreateDate    datetime, 
         @ExListID      varchar(32), 
         @ExActive      varchar(1)  
         
DECLARE  @Process TABLE   (FromListID  varchar(12), 
                           ToListID    varchar(12) NULL, 
                           PayeeID     varchar(12), 
                           UsersAcct   varchar(32), 
                           NameOnAcct  varchar(60),  
                           Active      varchar(1), 
                           MinDays     int, 
                           Method      varchar(1), 
                           Nickname    varchar(50), 
                           Processed   bit ) 

--Set the stored procedure name. 
SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
         
BEGIN TRY 
   --Get the records to be copied
   INSERT INTO @Process (FromListID, ToListID, PayeeID, UsersAcct, NameOnAcct, Active, MinDays, Method, Nickname, Processed) 
      SELECT 	u.PayeeListID, 
               NULL, 
               u.PayeeID, 
               u.UsersAccountAtPayee, 
               u.NameOnAccount, 
               u.Active, 
               u.MinDaystoPay, 
               u.PaymentMethod, 
               n.NickName, 
               0           
      FROM User_Payee u WITH (NOLOCK) 
         LEFT JOIN wc_NickNames n WITH (NOLOCK) ON u.PayeeListID = n.PayeeListID and u.MemberID = n.MemberID
      WHERE  u.MemberID = @From 
         AND u.Active = 'Y'
         
   --Get the number of records to be copied
   SELECT @Count = COUNT(FromListID) 
      FROM @Process 
      
   --Set the datetime variable so all change records go in with the exact same created datetime
   SET @CreateDate = GETDATE()
   
   BEGIN TRANSACTION
   WHILE @Count > 0 
   BEGIN 
      --Get a user payee record to be copied from the table variable
      SELECT TOP 1 @FromListID = FromListID,  
                  @PayeeID = PayeeID, 
                  @UsersAcct = UsersAcct, 
                  @NameOnAcct = NameOnAcct, 
                  @Active = Active, 
                  @MinDays = MinDays, 
                  @Method = Method, 
                  @Nickname = Nickname 
         FROM @Process 
         WHERE Processed = 0  
     
     --Check if record exists
     SELECT @ExListID = PayeeListID, 
            @ExActive = Active  
         FROM User_Payee WITH (NOLOCK) 
         WHERE MemberID = @To   
            AND PayeeID = @PayeeID 
            AND UsersAccountAtPayee = @UsersAcct 
            AND NameOnAccount = @NameOnAcct
            
      --If the record does not already exists, then copy it 
      IF (@ExListID IS NULL) 
      BEGIN 
         --Get a new PayeeListID          
         EXEC GetID 'PayeeListID', @NewListID OUTPUT
            
         --Insert the user_payee record
         INSERT INTO User_Payee (PayeeListID, MemberID, PayeeID, CreatedOn, LastUpdatedOn, 
                                 UsersAccountAtPayee, NameOnAccount, Active, MinDaystoPay, PaymentMethod) 
               OUTPUT INSERTED.PayeeListID, INSERTED.MemberID, INSERTED.PayeeID, INSERTED.CreatedOn, INSERTED.LastUpdatedOn, 
                        INSERTED.UsersAccountAtPayee, INSERTED.NameOnAccount, INSERTED.Active, INSERTED.MinDaystoPay, INSERTED.PaymentMethod, 
                        1, ('Copied From Account ' + @From +  '; ListID ' + @FromListID ), 'C', @CreateDate, @UpdatedBy, @From, @FromListID   
               INTO User_Payee_Change_History 
            VALUES (@NewListID, @To, @PayeeID, @CreateDate, @CreateDate, @UsersAcct, @NameOnAcct, @Active, @MinDays, @Method)
            
            --If a nickname exists for this user_payee, insert the nickname record
         IF (@Nickname IS NOT NULL)
            INSERT INTO wc_NickNames (PayeeListID, NickName, MemberID) 
                  OUTPUT INSERTED.PayeeListID, INSERTED.NickName, INSERTED.MemberID, 
                        'C', @CreateDate, @UpdatedBy  
                  INTO wc_NickNames_Change_History 
               VALUES (@NewListID, @Nickname, @To)
      END 
      ELSE
      BEGIN
         --If the record does exist and is not active, activate it
         IF (@ExActive != 'Y')
         BEGIN
            UPDATE User_Payee
               SET Active = 'Y', 
                  LastUpdatedOn = @CreateDate 
               OUTPUT DELETED.PayeeListID, DELETED.MemberID, DELETED.PayeeID, DELETED.CreatedOn, DELETED.LastUpdatedOn, 
                        DELETED.UsersAccountAtPayee, DELETED.NameOnAccount, DELETED.Active, DELETED.MinDaystoPay, DELETED.PaymentMethod, 
                        1, ('Copied From Account ' + @From +  '; ListID ' + @FromListID ), 'C', @CreateDate, @UpdatedBy, @From, @FromListID   
               INTO User_Payee_Change_History 
            WHERE PayeeListID = @ExListID
            
            SET @NewListID = @ExListID 
         END
         SET @ExListID = NULL      
      END
      
      --Update the processing count and table
      SET @Count = @Count - 1
      UPDATE @Process 
         SET ToListID = @NewListID, 
             Processed = 1 
         WHERE FromListID = @FromListID
      SET @NewListID = NULL 
   END
   
   COMMIT TRANSACTION
   
   --Return the data in the table variable
   SELECT   t.FromListID,  
            t.ToListID, 
            t.PayeeID, 
            p.Name as PayeeName, 
            t.UsersAcct, 
            t.NameOnAcct, 
            t.Active, 
            t.MinDays, 
            t.Method, 
            t.Nickname, 
            CASE WHEN t.ToListID IS NULL THEN 'No' 
               ELSE 'Yes' 
            END as Copied 
      FROM @Process t INNER JOIN Payee p WITH (NOLOCK) ON t.PayeeID = p.PayeeID 
   ORDER BY t.ToListID, p.Name 
   
   SELECT   t.FromListID,  
            t.ToListID, 
            t.PayeeID, 
            p.Name as PayeeName, 
            t.UsersAcct, 
            t.NameOnAcct, 
            t.Active, 
            t.MinDays, 
            t.Method, 
            t.Nickname 
