/*
 ==================================================================
  SP Name:     admin_CopyUserPayeesReverse

  Description:	Reverses the copied user payee records for the   
               member id by setting the active field to N.

  Resources: 	User_Payee_Change_History
               User_Payee
  ------------------------------------------------------------------
  Accepts:		@MemberID varchar(32), 
               @ModifyDate datetime, 
               @UpdatedBy varchar(32)

  Returns:		 
   ==================================================================
   Created:  	07/14/2008
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE   PROCEDURE [dbo].[admin_CopyUserPayeesReverse] 
				         @MemberID   varchar(32), 
                     @ModifyDate datetime, 
                     @UpdatedBy  varchar(32)
AS

SET NOCOUNT ON

DECLARE  @SPName     varchar(255), 
         @ListID     varchar(12), 
         @FromListID varchar(12), 
         @FromMemID  varchar(32), 
         @Count      smallint, 
         @RevDate    datetime   
         
DECLARE  @Process TABLE   (ListID  varchar(12),
                           FromMemberID varchar(32), 
                           FromPayeeListID varchar(12),  
                           Processed   bit ) 

--Set the stored procedure name. 
SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID))
         
BEGIN TRY 
   --Get the records that are to be reversed
   INSERT INTO @Process (ListID, FromMemberID, FromPayeeListID, Processed) 
      SELECT 	PayeeListID, 
               CopiedFromMemberID, 
               CopiedFromPayeeListID, 
               0   
         FROM User_Payee_Change_History  WITH (NOLOCK) 
         WHERE  ModifyType = 'C' 
            AND MemberID = @MemberID  
            AND ModifyDate = @ModifyDate 
       
   --Get the number of records to be reversed.  
   SELECT @Count = COUNT(ListID) 
      FROM @Process 
      
   --Set the datetime variable so all change records go in with the exact same updated datetime
   SET @RevDate = GETDATE()
   
   BEGIN TRANSACTION
   WHILE @Count > 0 
   BEGIN 
      --Get a user payee record to be reversed from the table variable
      SELECT TOP 1 @ListID = ListID,
                   @FromMemID = FromMemberID, 
                   @FromListID = FromPayeeListID 
         FROM @Process 
         WHERE Processed = 0  
     
      --If the record  exists, then reverse it 
      IF EXISTS (SELECT PayeeListID 
                     FROM User_Payee WITH (NOLOCK) 
                     WHERE PayeeListID = @ListID) 
      BEGIN 
         --Reverse the copy by updating the user_payee record active field to 'N'
         UPDATE User_Payee 
            SET Active = 'N', 
                LastUpdatedOn = @RevDate  
            OUTPUT DELETED.PayeeListID, DELETED.MemberID, DELETED.PayeeID, DELETED.CreatedOn, 
                   DELETED.LastUpdatedOn, DELETED.UsersAccountAtPayee, DELETED.NameOnAccount, 
                   DELETED.Active, DELETED.MinDaystoPay, DELETED.PaymentMethod, 
                     1, ('Reverse Copy From Account ' + @FromMemID +  '; ListID ' + @FromListID ), 'U', @RevDate, @UpdatedBy, @FromMemID, @FromListID   
            INTO User_Payee_Change_History 
         WHERE PayeeListID = @ListID 
            
      END 
      
      --Update the processing count and table
      SET @Count = @Count - 1
      UPDATE @Process 
         SET Processed = 1 
         WHERE ListID = @ListID
   END
   
   COMMIT TRANSACTION
 
END TRY
BEGIN CATCH
   IF (@@TRANCOUNT > 0) OR ((XACT_STATE()) = -1)
      ROLLBACK TRANSACTION
   
	DECLARE	@errorInfo VARCHAR(7000)
	SET @errorInfo = CAST(('Msg = ' + ISNULL(ERROR_MESSAGE(), '')) AS VARCHAR(7000)) 
	EXEC spLog @errorInfo, @SPName
	
END CATCH 

