*/
CREATE  PROCEDURE [dbo].[admin_DeletePayee]
					      @PayeeID		char(12),
					      @user_type		tinyint,
					      @change_reason	char(255),
					      @updatedBy		varchar(32) 
AS

declare @return_status int, @Status int

set @return_status = dbo.admin_udfPayeeHasDependents(@PayeeID)

if @return_status = 0
	BEGIN
		DELETE FROM payee 
		   OUTPUT DELETED.PayeeID, DELETED.PayeeAliasID, DELETED.ProviderID, DELETED.[Name], 
		          DELETED.Address1, DELETED.Address2, DELETED.Address3, DELETED.City, 
		          DELETED.State, DELETED.PostalCode, DELETED.Country, DELETED.Phone, 
		          DELETED.Scope, DELETED.MinDaysToPay, DELETED.LastUpdatedOn, 
		          DELETED.LastUpdatedBy, DELETED.BankID, DELETED.BranchID, DELETED.AccountID, 
		          DELETED.AccountType, DELETED.PaymentMethod, DELETED.AccountKey, 
		          DELETED.EntryDate, DELETED.PayeeType, DELETED.BillerKey, DELETED.AddressKey, 
		          @user_type, @change_reason, 'D', GETDATE(), @updatedBy
		      INTO Payee_Change_History 
               (PayeeID, PayeeAliasID, ProviderID, [Name], Address1, Address2, 
               Address3, City, State, PostalCode, Country, Phone, Scope, MinDaysToPay, 
               LastUpdatedOn, LastUpdatedBy, BankID, BranchID, AccountID, AccountType, 
               PaymentMethod, AccountKey, EntryDate, PayeeType, BillerKey, AddressKey,	
               User_Type, Reason, ModifyType, ModifyDate, ModifiedBy) 
		   WHERE PayeeId = @PayeeId

		if (@@error <> 0) 
			select @status=-1 
		else 
			select @Status=0
	END
else
	select @Status=-100

select "Status"=@Status
return(@Status)

