CREATE  PROCEDURE [dbo].[admin_GetCombineHistoryChild]
                     @SearchType varchar(5), 
					      @SearchText varchar(40),
					      @LowDate smalldatetime, 
					      @HighDate smalldatetime    
AS

--Set the date parameters if they are null
IF @LowDate IS NULL
   --Using a low date of 1/1/2006 will obtain all combine history 
   --from the day that recording combine history was implemented;
   --since recording of combine history was not implemented until 2007.
   SELECT @LowDate = '01/01/2006'
IF @HighDate IS NULL 
  	 SELECT @HighDate = CONVERT(VARCHAR(10), (GETDATE() + 1), 101)
   ELSE
      SELECT @HighDate = CONVERT(VARCHAR(10), (@HighDate + 1), 101)

SELECT   @LowDate AS LowDate, 
         @HighDate AS HighDate 
INTO #PaymentDates 

--Create the temp table to hold the records on which combine history will be obtained.
CREATE TABLE #Payees (PayeeID varchar(12), ModifyDate smalldatetime) 

--Fill the temp table with the PayeeID and ModifyDate of the combine  
--history records that match the criteria passed in.
IF @SearchType = 'Name'
      INSERT INTO #Payees 
         SELECT   ch.PayeeID,
                  ch.ModifyDate 
         FROM Payee_Change_History ch WITH (NOLOCK) 
	            INNER JOIN #PaymentDates d ON ch.ModifyDate BETWEEN d.LowDate AND d.HighDate 
         WHERE ch.Name LIKE (@SearchText + '%') 
            AND ch.CombinedInto IS NOT NULL  

ELSE IF @SearchType = 'Zip'
      INSERT INTO #Payees 
         SELECT   ch.PayeeID,
                  ch.ModifyDate 
         FROM Payee_Change_History ch WITH (NOLOCK) 
	            INNER JOIN #PaymentDates d ON ch.ModifyDate BETWEEN d.LowDate AND d.HighDate 
         WHERE ch.PostalCode LIKE (@SearchText + '%') 
            AND ch.CombinedInto IS NOT NULL  

ELSE IF @SearchType = 'ID' 
      INSERT INTO #Payees 
         SELECT   ch.PayeeID,
                  ch.ModifyDate 
         FROM Payee_Change_History ch WITH (NOLOCK) 
	            INNER JOIN #PaymentDates d ON ch.ModifyDate BETWEEN d.LowDate AND d.HighDate 
         WHERE ch.PayeeID = @SearchText
            AND ch.CombinedInto IS NOT NULL  

--Get payee combine change history for payees combined into other payees
   SELECT   ch.ModifyDate as LastUpdatedOn, 
            ch.ModifiedBy as LastUpdatedBy, 
            ch.Reason, 
            ut.Description as UserDesc, 
            CASE ch.ModifyType 
               WHEN 'U' THEN 'Updated'
               WHEN 'D' THEN 'Deleted'
               ELSE ''
            END as ModifyType, 
            ch.PayeeID, 
            ch.PayeeAliasID, 
            pr.ProviderName, 
            ch.Name, 
            dbo.admin_udfConcatAddress(ch.Address1, ch.Address2, ch.Address3, ch.City, ch.State, ch.PostalCode, ch.Country, ch.Phone) as Address, 
            ch.Scope, 
            ch.PaymentMethod, 
            ch.CombinedInto, 
            m.Name as MasterName, 
            dbo.admin_udfConcatAddress(m.Address1, m.Address2, m.Address3, m.City, m.State, m.PostalCode, m.Country, m.Phone) as MasterAddress 
      FROM Payee_Change_History ch WITH (NOLOCK) 
            INNER JOIN User_Type ut WITH (NOLOCK) ON ch.User_Type = ut.UserType 
            INNER JOIN Provider pr WITH (NOLOCK) ON ch.ProviderID = pr.ProviderID 
	         INNER JOIN #Payees p ON ch.PayeeID = p.PayeeID AND ch.ModifyDate = p.ModifyDate 
	         LEFT JOIN Payee m WITH (NOLOCK) ON ch.CombinedInto = m.PayeeID 
   ORDER BY LastUpdatedOn DESC
 
   
--Get user payee information for the payees combined into other payees
SELECT   ch.ModifyDate as LastUpdatedOn, 
         ch.ModifiedBy as LastUpdatedBy, 
         ch.Reason, 
         ut.Description as UserDesc, 
         CASE ch.ModifyType 
            WHEN 'U' THEN 'Updated'
            WHEN 'D' THEN 'Deleted'
            ELSE ''
         END as ModifyType, 
         ch.NameOnAccount, 
         ch.UsersAccountAtPayee, 
         ch.PaymentMethod, 
         ch.PayeeListID, 
         ch.PayeeID, 
         ch.MemberID 
   FROM User_Payee_Change_History ch WITH (NOLOCK) 
      INNER JOIN User_Type ut WITH (NOLOCK) ON ch.User_Type = ut.UserType 
      INNER JOIN #Payees p WITH (NOLOCK) ON ch.PayeeID = p.PayeeID 
   
--Delete temp tables    
DROP TABLE #PaymentDates
DROP TABLE #Payees 

GO
