/*
 ==============================================================================================
  SP Name:        [admin_GetFailedOnUs]

  Description:   Obtains the failed ONUS transactions.

  Resources:	Payment_History
				User_Payee
				Payee
				Provider 
  -----------------------------------------------------------------------------------------------
  Accepts:	@Start smalldatetime, 
            @End   smalldatetime  

  Returns:	pr.[ProviderID], 
			h.[PaymentID], 
			h.[PayeeID],  
			h.[PayeeListID], 
			h.[MemberID],   
			h.[Account], 
			h.[Amount],
			h.[ProcessedDate],
			h.[FailedDate], 
			h.[RecPaymentID], 
			h.[StatusCode], 
			h.[UsersAccountAtPayee], 
			h.[NameOnAccount], 
			h.[MemberFirstName], 
			h.[MemberLastName]   
 =================================================================================================
	Created:	07/19/2011
	     By:	GNordstrom

  ------------------------------------------------------------------------------------------------
  Modifications: 
	12/11/2013	HLiou	Use the ProviderID from Payment_History instead of Payees providerid

 =================================================================================================
*/
CREATE  PROCEDURE [dbo].[admin_GetFailedOnUs]
		@Start smalldatetime, 
		@End   smalldatetime
AS

SET NOCOUNT ON
DECLARE  @SPName   varchar(255)

BEGIN TRY
   SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID)); 

   SELECT pr.[ProviderID], 
		h.[PaymentID], 
		h.[PayeeID],  
		h.[PayeeListID], 
		h.[MemberID],   
		h.[Account], 
		h.[Amount]/100.0 as [Amount],
		h.[ProcessedDate],
		h.[FailedDate], 
		h.[RecPaymentID], 
		h.[StatusCode], 
		c.[Description] as [Status], 
		h.[UsersAccountAtPayee], 
		h.[NameOnAccount], 
		h.[MemberFirstName], 
		h.[MemberLastName]   
    FROM Payment_History h WITH (NOLOCK) 
         INNER JOIN User_Payee u WITH (NOLOCK) ON h.PayeeListID = u.PayeeListID 
         INNER JOIN Payee p WITH (NOLOCK) ON u.PayeeID = p.PayeeID  
         INNER JOIN Provider pr WITH (NOLOCK) ON h.ProviderID = pr.ProviderID 
         INNER JOIN status_code c WITH (NOLOCK) ON  h.statuscode=c.statuscode
    WHERE h.FailedDate IS NOT NULL   
         AND pr.Active = 'Y'  
	     AND h.StatusCode in (203,220,221)
         AND pr.ProviderID IN (101)
         AND CONVERT(varchar, h.ProcessedDate, 112) BETWEEN CONVERT(varchar, @Start, 112) AND CONVERT(varchar, @End, 112) 

END TRY
BEGIN CATCH
   DECLARE   @ErrorMessage  nvarchar(4000), 
                     @ErrorSeverity    int, 
                     @ErrorState         int, 
                     @ErrorNum          int;  
        
   SET @ErrorNum  = ERROR_NUMBER();   
   SELECT  @ErrorMessage = CONVERT(nvarchar, @ErrorNum) + ':' + ERROR_MESSAGE(),
                  @ErrorSeverity = ERROR_SEVERITY(),
                  @ErrorState = ERROR_STATE(); 

    RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)
END CATCH    




