CREATE PROCEDURE [dbo].[admin_GetGeneratorStatusByRunDate]
         @DateProcessed datetime
AS

SET NOCOUNT ON

SELECT 	r.ID as RunLogID,
		   g.ID as GeneratorID, 
		   g.RunID as RunID,
		   g.StartTime,
	      g.EndTime,
	      gs.Description As Status,
	      g.Message
