/*
 ==================================================================================
  SP Name:     [admin_GetMonthYearlyRecap]

  Description: Obtains the sum and count by provider for the Month, year
				   submitted 

  Resources: 	payment_history
				   Payee
				   Provider				  
  ---------------------------------------------------------------------------------
  Accepts:		@Month varchar(20), @Year varchar(20)	

  Returns:		
   ================================================================================
   Created:  	Aili Cheng
   By:    		05/07
  ---------------------------------------------------------------------------------
  Modifications: 
	07/12/2007  GNordstrom  Added status code 99.
	12/11/2013  HLiou		Return History ProviderID and not payee's providerid
===================================================================================
*/
 
CREATE PROCEDURE [dbo].[admin_GetMonthYearlyRecap]
  @Month varchar(20),
  @Year varchar(20)
AS
SET NOCOUNT ON

IF (@Month != '')--- Monthly report
	  SELECT Month(ph.processeddate)As Month,
			 count_big(ph.paymentid) AS NumPayments, 
			 convert(money,(convert(bigint,sum(convert(bigint,ph.amount)))/100.)) AS TotalAmount,
			 pv.ProviderName AS Provider
	  FROM payment_history ph WITH (NOLOCK)
		  INNER JOIN payee p WITH (NOLOCK) ON ph.payeeid = p.payeeid 
		  INNER JOIN Provider pv WITH (NOLOCK) on ph.ProviderID = pv.ProviderID
	  WHERE pv.active = 'Y'
		AND StatusCode in (99, 101, 102)
	 	AND Month(ph.processeddate)= @Month 
		AND Year(ph.processeddate) = @Year
 	  GROUP BY pv.ProviderName, Month(ph.processeddate)
      ORDER BY Provider

ELSE--IF (@Month = '') Yearly report
	  SELECT year(ph.processeddate)AS Year,
			 count(ph.paymentid) AS NumPayments, 
			 convert(money,(convert(bigint,sum(convert(bigint,ph.amount)))/100.)) AS TotalAmount,
			 pv.ProviderName AS Provider
	  FROM payment_history ph WITH (NOLOCK)
		  INNER JOIN payee p WITH (NOLOCK) ON ph.payeeid = p.payeeid 
		  INNER JOIN Provider pv WITH (NOLOCK) on ph.ProviderID = pv.ProviderID
	  WHERE pv.active = 'Y'
		AND StatusCode in (99, 101, 102)
		AND year(ph.processeddate) = @Year
	  GROUP BY pv.providerName, year(ph.processeddate)
	  ORDER BY Provider




