/*
 ==================================================================
  SP Name:       admin_GetOnUsPostings

  Description:   Obtains the OnUs payments and their posting history.

  Resources:     OnUsLoans
				 OnUsLoansTracking
  ------------------------------------------------------------------
  Accepts:	     @startDate  smalldatetime, 
                 @endDate smalldatetime   
               
  Returns:		 2 recordsets
   ==================================================================
   Created:      05/09/2013
   By:    	     GNordstrom
  ------------------------------------------------------------------
  Modifications: 
   2/12/2014 HLiou	Renamed OnUsLoan table to OnUsPayments table.

==================================================================
*/
CREATE PROCEDURE [dbo].[admin_GetOnUsPostings] 
                       @startDate  smalldatetime, 
                       @endDate smalldatetime
AS

SET NOCOUNT ON

DECLARE	@SPName   varchar(255)                     

BEGIN TRY
	SET @SPName = CONVERT(varchar, OBJECT_NAME(@@PROCID));

	SELECT [PaymentID]
		  ,[MemberID]
		  ,[AccountID]
		  ,[LoanID]
		  ,[Amount]
		  ,[Comment]
		  ,[GLCode]
		  ,[RunID]
		  ,ISNULL([ErrorCode], '') AS [ErrorCode] 
		  ,ISNULL([ErrorDesc], '') AS [ErrorDesc] 
		  ,ISNULL([SourceApp], '') AS [SourceApp] 
		  ,[EntryDate]
		  ,[ModifiedDate]
		  ,[ModifiedBy]	  
	FROM  [OnUsPayments] 
	WHERE CONVERT(varchar, [EntryDate], 112) BETWEEN CONVERT(varchar, @startDate, 112) AND CONVERT(varchar, @endDate, 112) 
	ORDER BY [PaymentID]
   	   
	SELECT t.[SeqNo]
		  ,t.[PaymentID]
		  ,t.[MemberID]
		  ,t.[AccountID]
		  ,t.[LoanID]
		  ,t.[Amount]
		  ,t.[Comment]
		  ,t.[GLCode]
		  ,t.[RunID]
		  ,ISNULL(t.[ErrorCode], '') AS [ErrorCode] 
		  ,ISNULL(t.[ErrorDesc], '') AS [ErrorDesc] 
		  ,ISNULL(t.[SourceApp], '') AS [SourceApp] 
		  ,t.[EntryDate]
		  ,t.[ModifiedDate]
		  ,t.[ModifiedBy]
		  ,ISNULL(t.[ChangeReason], '') AS [ChangeReason] 
	  FROM [OnUsPaymentsTracking] t 
			INNER JOIN 	[OnUsPayments] l ON t.[PaymentID] = l.[PaymentID]   
	WHERE CONVERT(varchar, l.[EntryDate], 112) BETWEEN CONVERT(varchar, @startDate, 112) AND CONVERT(varchar, @endDate, 112) 
	ORDER BY [PaymentID], [SeqNo]
	                              
END TRY
BEGIN CATCH 
   DECLARE   @ErrorMessage  nvarchar(4000), 
                     @ErrorSeverity    int, 
                     @ErrorState         int, 
                     @ErrorNum          int;  
        
   SET @ErrorNum  = ERROR_NUMBER();   
   SELECT  @ErrorMessage = CONVERT(nvarchar, @ErrorNum) + ':' + ERROR_MESSAGE(),
                  @ErrorSeverity = ERROR_SEVERITY(),
                  @ErrorState = ERROR_STATE(); 

   EXEC [spLog]  @ErrorMessage, @SPName	  
   RAISERROR (@ErrorMessage, @ErrorSeverity, @ErrorState)
END CATCH


