/*
 ==================================================================
  SP Name:     dbo.admin_GetPayee

  Description: Obtains data for a single payee based on the criteria
				   passed in.

  Resources: 	Payee
				   Provider
				   user_payee
  ------------------------------------------------------------------
  Accepts:		@iListType  int,
				   @sSearch1     varchar(40),
				   @sSearch2     varchar(40)=null

  Returns:		varies bassed on list type passed in but at a minimum: 
			         p1.PayeeID,
			         p1.PayeeAliasID,
			         p1.ProviderID, 
			         p1.Name,
			         p1.Address1, 
			         p1.Address2, 
			         p1.Address3,
			         p1.City,
			         p1.State,
			         p1.PostalCode,
			         p1.Country,
			         p1.Phone,
			         p1.Scope,
			         p1.MinDaysToPay,
			         p1.LastUpdatedOn,
			         p1.LastUpdatedBy,
			         p1.BankID,
			         p1.BranchID,
			         p1.AccountID,
			         p1.AccountType,
			         p1.PaymentMethod,
			         p1.AccountKey,
			         p1.EntryDate,
			         p1.PayeeType,
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	12/26/2005	GNordstrom	Copied ListPayee to this new proc name.
	03/27/2007  GNordstrom  Modified @iListType 2 so that it returned additional 
	                        fields and eliminated the return of obsolete or 
	                        unnecessary fields.  Also eliminated the MemberID 
	                        from the where clause condition for @iListType 2.
==================================================================
*/
CREATE PROCEDURE [dbo].[admin_GetPayee]
					@iListType  int,
					@sSearch1     varchar(40),
					@sSearch2     varchar(40)=null
AS

DECLARE @dtLo datetime, @dtHi datetime

IF @iListType = 1         /* by Payee Id  */	
	SELECT 	p1.PayeeID,
			   p1.PayeeAliasID,
			   p1.ProviderID, 
			   p1.Name,
			   p1.Address1, 
			   p1.Address2, 
			   p1.Address3,
			   p1.City,
			   p1.State,
			   p1.PostalCode,
			   p1.Country,
			   p1.Phone,
			   p1.Scope,
			   p1.MinDaysToPay,
			   p1.LastUpdatedOn,
			   p1.LastUpdatedBy,
			   p1.BankID,
			   p1.BranchID,
			   p1.AccountID,
			   p1.AccountType,
			   p1.PaymentMethod,
			   p1.AccountKey,
			   p1.EntryDate,
			   p1.PayeeType,
			   p2.name as AliasName, 
			   pr.ProviderName 
		FROM payee p1 WITH (NOLOCK) 
			LEFT JOIN payee p2  WITH (NOLOCK) ON p1.PayeeAliasId = p2.payeeId
			INNER JOIN provider pr  WITH (NOLOCK) ON p1.ProviderId = pr.ProviderId
		WHERE p1.PayeeId = @sSearch1
ELSE IF @iListType = 2         /* by payee list Id  */
	SELECT 	p.PayeeID,
			   p.PayeeAliasID,
			   p.ProviderID, 
			   pr.ProviderName, 
			   p.Name, 
			   dbo.admin_udfConcatAddress(p.Address1, p.Address2, p.Address3, p.City, p.State, p.PostalCode, p.Country, '') as Address, 
			   p.Phone,
			   p.Scope,
			   p.LastUpdatedOn,
			   p.LastUpdatedBy,
			   p.EntryDate,
			   u.PayeeListID, 
			   u.MemberID, 
			   u.UsersAccountAtPayee, 
			   u.NameOnAccount, 
			   u.PaymentMethod, 
			   u.CreatedOn, 
			   u.LastUpdatedOn, 
			   ISNULL(n.NickName, '') as NickName 
		FROM payee p WITH (NOLOCK)  
			INNER JOIN user_payee u  WITH (NOLOCK) ON p.payeeid = u.payeeid 
			INNER JOIN provider pr WITH (NOLOCK) ON p.ProviderID = pr.ProviderID 
			LEFT JOIN wc_NickNames n WITH (NOLOCK) ON u.PayeeListID = n.PayeeListID 
		WHERE u.PayeeListid = @sSearch1 
ELSE IF @iListType=3         /* by MemberId */
	SELECT 	p.PayeeID,
			   p.PayeeAliasID,
			   p.ProviderID, 
			   p.Name,
			   p.Address1, 
			   p.Address2, 
			   p.Address3,
			   p.City,
			   p.State,
			   p.PostalCode,
			   p.Country,
			   p.Phone,
			   p.Scope,
			   p.MinDaysToPay,
			   p.LastUpdatedOn,
			   p.LastUpdatedBy,
			   p.BankID,
			   p.BranchID,
			   p.AccountID,
			   p.AccountType,
			   p.PaymentMethod,
			   p.AccountKey,
			   p.EntryDate,
			   p.PayeeType,  
			   u.PayeeListId, 
			   u.CreatedOn, 
			   u.LastUpdatedOn as UserLastUpdatedOn, 
			   u.CreatedOn as UserCreatedOn 
		FROM payee p  WITH (NOLOCK) 
			INNER JOIN user_payee u  WITH (NOLOCK) ON p.payeeid=u.payeeid 
		WHERE u.MemberId = @sSearch1
ELSE IF @iListType = 4         /* by Alias Id  */	
	BEGIN
		CREATE TABLE #p1 (payeeid char(12))
		INSERT INTO #p1 
			SELECT payeeid 
				FROM payee WITH (NOLOCK) 
				WHERE PayeeAliasId = @sSearch1
		SELECT 	p1.PayeeID,
				   p1.PayeeAliasID,
				   p1.ProviderID, 
				   p1.Name,
				   p1.Address1, 
				   p1.Address2, 
				   p1.Address3,
				   p1.City,
				   p1.State,
				   p1.PostalCode,
				   p1.Country,
				   p1.Phone,
				   p1.Scope,
				   p1.MinDaysToPay,
				   p1.LastUpdatedOn,
				   p1.LastUpdatedBy,
				   p1.BankID,
				   p1.BranchID,
				   p1.AccountID,
				   p1.AccountType,
				   p1.PaymentMethod,
				   p1.AccountKey,
				   p1.EntryDate,
				   p1.PayeeType,
				   p2.name as AliasName, 
				   pr.ProviderName 
			FROM payee p1 WITH (NOLOCK) 
				INNER JOIN #p1 WITH (NOLOCK) ON p1.payeeid=#p1.payeeid
				LEFT JOIN payee p2 WITH (NOLOCK) ON p1.PayeeAliasId = p2.payeeId
				INNER JOIN provider pr WITH (NOLOCK) ON p1.ProviderId = pr.ProviderId
			ORDER BY p1.payeeid
	END
ELSE IF @iListType = 5         /* by date  */	
	BEGIN
		SELECT	 @dtLo = CONVERT(datetime, @sSearch1),
				@dtHi = DATEADD(day, 1, CONVERT(datetime, @sSearch1))
		SELECT 	p1.PayeeID,
				   p1.PayeeAliasID,
				   p1.ProviderID, 
				   p1.Name,
				   p1.Address1, 
				   p1.Address2, 
				   p1.Address3,
				   p1.City,
				   p1.State,
				   p1.PostalCode,
				   p1.Country,
				   p1.Phone,
				   p1.Scope,
				   p1.MinDaysToPay,
				   p1.LastUpdatedOn,
				   p1.LastUpdatedBy,
				   p1.BankID,
				   p1.BranchID,
				   p1.AccountID,
				   p1.AccountType,
				   p1.PaymentMethod,
				   p1.AccountKey,
				   p1.EntryDate,
				   p1.PayeeType,
				   pr.ProviderName 
			FROM payee p1 WITH (NOLOCK) 
				INNER JOIN provider pr  WITH (NOLOCK) ON p1.ProviderId = pr.ProviderId
			WHERE p1.LastUpdatedOn BETWEEN @dtLo AND @dtHi
	END
ELSE IF @iListType = 6		/* by date range */
	BEGIN
		SELECT 	@dtLo = CONVERT(datetime, @sSearch1), 
				@dtHi = CONVERT(datetime, @sSearch2)
		IF DATEPART(hour, @dtHi) = 0 AND datepart(minute, @dtHi) = 0 AND DATEPART(second, @dtHi) = 0 
			SELECT @dtHi = DATEADD(day, 1, @dtHi)
		SELECT 	p1.PayeeID,
				   p1.PayeeAliasID,
				   p1.ProviderID, 
				   p1.Name,
				   p1.Address1, 
				   p1.Address2, 
				   p1.Address3,
				   p1.City,
				   p1.State,
