/*
 ==================================================================
  Stored Procedure Name: dbo.admin_GetPayees

  Description: 	Obtains payees for the criteria passed in.

  Resources: 	payee
				user_payee
				dbo.admin_udfPayeeHasDependents
  ------------------------------------------------------------------
  Accepts:		@strSearch          varchar(40)='',	
				@iScope             int=0,		
				@iSearchType        int=1,
				@iMinNumMembers     int=0,
				@bFilterAliased     bit=0,
				@strLastUpdate      varchar(32)=''

  Returns:		e.payeeid,
				e.name,
				e.PayeeAliasId,
				e2.name as AliasName,
				count(u.payeelistid) as NumMembers,
				dbo.admin_udfConcatAddress(e.address1, e.address2, e.address3, e.city, e.state, e.PostalCode, e.country) as Address,
				e.phone,
				e.scope,
				e.LastUpdatedOn,
				e.LastUpdatedBy, 
				e.ProviderID, 
				e.Address1, 
				e.Address2, 
				e.Address3,
				e.City,
				e.State,
				e.PostalCode,
				e.Country,
				e.MinDaysToPay,
				e.BankID,
				e.BranchID,
				e.AccountID,
				e.AccountType,
				e.PaymentMethod,
				e.AccountKey,
				dbo.admin_udfPayeeHasDependents(e.payeeid) as DepCount    
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	12/22/2005	GNordstrom	Copied FindPayees to this new proc name.
	12/22/2005	GNordstrom	Removed the sort functionality because the
							      sorting by other fields can be done in the 
							      application.  Added returning the address 	
							      fields as one field with <br> in between lines.
	12/26/2005	GNordstrom	Added return of additional fields.
	09/18/2007  GNordstrom  Added new search type:  PayeeListID.
===================================================================
*/
CREATE           PROCEDURE [dbo].[admin_GetPayees]
					@strSearch          varchar(40)='',	-- required to have strSearch or iMinNumMembers
					@iScope             int=0,		-- or scope=GLOBAL.
					@iSearchType        int=1,
					@iMinNumMembers     int=0,
					@bFilterAliased     bit=0,
					@strLastUpdate      varchar(32)=''
AS

create table #p1 ( pid char(12) )
if @strSearch > ""
	if @iSearchType = 1  -- payee id search
		insert into #p1 values (@strSearch)	-- we already know which payee we are looking for.
	else if @iSearchType = 2  -- payee name search
		insert into #p1 
			select e.payeeid as pid 
				from payee e WITH (NOLOCK) 
				where e.name like @strSearch
	else if @iSearchType = 3   -- Phone Number Search
		insert into #p1 
			select e.payeeid as pid 
				from payee e WITH (NOLOCK) 
				where e.phone like @strSearch 
	else if @iSearchType = 4   -- Member Id Search
		insert into #p1 
			select e.payeeid as pid 
				from payee e WITH (NOLOCK) inner join user_payee u WITH (NOLOCK) 
						on e.payeeid=u.payeeid 
							and u.MemberId = @strSearch 
	else if @iSearchType = 5  -- PayeeListID Search
	   INSERT INTO  #p1 
			SELECT u.PayeeID as pid 
				FROM User_Payee u WITH (NOLOCK) 
				WHERE u.PayeeListID = @strSearch  
	else goto badparams	-- need searchtype between 1 and 5

else if @iMinNumMembers > 0	-- if passed without a strSearch
	insert into #p1 
		select payeeId as pid 
			from user_payee WITH (NOLOCK) 
			group by payeeid
				having count(payeelistid) > @iMinNumMembers
else if @iScope = 2	-- just GLOBAL payees if passed without a strSearch
	insert into #p1 
		select payeeid as pid 
			from payee WITH (NOLOCK) 
			where scope = 'GLOBAL'
else if @strLastUpdate > ''
	insert into #p1 
		select payeeid as pid 
			from payee WITH (NOLOCK) 
			where LastUpdatedOn > @strLastUpdate
else goto badparams	-- we must have strSearch, MinNumMembers, 
			-- a LastUpdate date or GLOBAL scope to run search.

select 	e.payeeid,
		e.name,
		e.PayeeAliasId,
		e2.name as AliasName,
		count(u.payeelistid) as NumMembers,
		dbo.admin_udfConcatAddress(e.address1, e.address2, e.address3, e.city, e.state, e.PostalCode, e.country, '') as Address,
		e.phone,
		e.scope,
		e.LastUpdatedOn,
		e.LastUpdatedBy, 
		e.ProviderID, 
		e.Address1, 
		e.Address2, 
		e.Address3,
		e.City,
		e.State,
		e.PostalCode,
		e.Country,
		e.MinDaysToPay,
		e.BankID,
		e.BranchID,
		e.AccountID,
		e.AccountType,
		e.PaymentMethod,
		e.AccountKey, 
		dbo.admin_udfPayeeHasDependents(e.payeeid) as DepCount 
	from payee e WITH (NOLOCK) 
		inner join #p1 on e.payeeid=#p1.pid
		left join payee e2 WITH (NOLOCK) on e.PayeeAliasId=e2.PayeeId
		left join user_payee u WITH (NOLOCK) on e.payeeid=u.payeeid
	where e.LastUpdatedOn > @strLastUpdate
		and ( @iScope=0 
				or (@iScope=1 and e.scope = 'USER') 
				or (@iScope=2 and e.scope='GLOBAL'))
		and (@bFilterAliased=0 or e.PayeeAliasId='')
	group by 	e.payeeid,
				e.name,
				e.PayeeAliasId,
				e2.name,
				e.address1,
				e.address2,
				e.address3, 
				e.city, 
				e.state, 
				e.PostalCode,
				e.country,
				e.phone,
				e.scope,
				e.LastUpdatedOn,
				e.LastUpdatedBy, 
				e.ProviderID, 
				e.Address1, 
				e.Address2, 
				e.Address3,
				e.City,
				e.State,
				e.PostalCode,
				e.Country,
				e.MinDaysToPay,
				e.BankID,
				e.BranchID,
				e.AccountID,
				e.AccountType,
				e.PaymentMethod,
				e.AccountKey    
		having ( @iMinNumMembers=0 or count(u.payeelistid) >= @iMinNumMembers ) 
	order by e.payeeid
if (@@ERROR <> 0) GOTO on_error
	

RETURN (0)

on_error:
	RETURN (-1)
badparams:
	print 'Insufficient or bad parameter(s) for query'
	return (-100)
GO
