*/
CREATE     PROCEDURE [dbo].[admin_GetPayeesByIds]
						@payeeIds varchar(280) 
AS

SET NOCOUNT ON

DECLARE @intChar smallint
DECLARE @tblTemp TABLE (payeeID varchar(12))

WHILE Len(@payeeIds) > 0 
	BEGIN
		INSERT INTO @tblTemp VALUES (SUBSTRING(@payeeIds, 1, 12))
		SET @intChar = CHARINDEX(',', RTRIM(LTRIM(@payeeIds)), 1)
	
		IF (@intChar > 0)
			SET @payeeIds = ltrim(SUBSTRING(@payeeIds, (@intChar + 1), len(@payeeIds)))
		ELSE 
			BREAK
	END

SELECT  P.PayeeID,
		LTRIM(P.Name) as PayeeName,
		dbo.admin_udfConcatAddress(P.Address1, P.Address2, P.Address3, P.City, P.State, P.PostalCode, P.Country, P.Phone) as Address, 
		ISNULL(P.PaymentMethod, '') as PaymentMethod 
FROM Payee p  WITH (NOLOCK) 
WHERE p.PayeeID IN (SELECT payeeID FROM @tblTemp)
ORDER BY p.PayeeID 


SET NOCOUNT OFF







