*/
CREATE  PROCEDURE [dbo].[admin_GetPaymentChgHistory]
					@paymentID varchar(10), 
					@paymentType char(1)   
AS

If @paymentType = "S"
	
	SELECT 	ch.LastUpdatedOn, 
			   ch.LastUpdatedBy, 
			   ch.Reason, 
			   ut.Description as UserDesc, 
			   '' as ModifyType, 
			   '' as Account, 
			   '' as Amount, 
			   null as ProcessDate, 
			   null as DueDate, 
			   '' as NumPayments, 
			   '' as Frequency, 
			   '' as Memo, 
			   ch.PaymentID, 
			   '' as BillReference, 
			   '' as SourceApplication, 
			   '' as PayeeID, 
			   '' as PayeeName, 
			   '' as Status, 
            '' as InitialAmount, 
            '' as FinalAmount,              
            '' as LastProcessedDate, 
            '' as InitialDueDate,  
            '' as Active, 
            '' as TrsfSource,
			null as DeliveryDate  
	   FROM Change_History ch WITH (NOLOCK) 
	         INNER JOIN User_Type ut WITH (NOLOCK) ON ch.UserType = ut.UserType
	   WHERE ch.PaymentID = @paymentID 
	
	UNION
	
   SELECT   ch.ModifyDate as LastUpdatedOn, 
            ch.ModifiedBy as LastUpdatedBy, 
            ch.Reason, 
            ut.Description as UserDesc,  
            CASE ch.ModifyType 
               WHEN 'U' THEN 'Updated'
               WHEN 'D' THEN 'Deleted'
               ELSE ''
            END as ModifyType, 
            ch.Account, 
            LTRIM(CONVERT(char(25), CONVERT(money, CONVERT(real, ch.Amount)/100))) as Amount, 
            ch.WillProcessDate as ProcessDate, 
            ch.DueDate, 
            '' as NumPayments, 
            '' as Frequency, 
            ch.Memo, 
            ch.PaymentID, 
            ch.BillReference, 
