            p.PayeeID, 
            p.Name as PayeeName, 
            sc.Description as Status, 
            '' as InitialAmount, 
            '' as FinalAmount,              
            '' as LastProcessedDate, 
            '' as InitialDueDate, 
            '' as Active, 
            ISNULL(RIGHT(ch.TrsfSource, 4), '') as TrsfSource,
			ch.DeliveryDate            
      FROM Payment_Change_History ch WITH (NOLOCK) 
            INNER JOIN User_Type ut WITH (NOLOCK) ON ch.User_Type = ut.UserType	
            INNER JOIN (User_Payee up WITH (NOLOCK) 
               INNER JOIN Payee p WITH (NOLOCK) ON up.PayeeID = p.PayeeID) 
                     ON ch.PayeeListID = up.PayeeListID AND ch.MemberID = up.MemberID  
            INNER JOIN Status_Code sc WITH (NOLOCK) ON ch.StatusCode = sc.StatusCode 
	   WHERE ch.PaymentID = @paymentID 
	ORDER BY LastUpdatedOn DESC

ELSE

	SELECT 	ch.LastUpdatedOn, 
			   ch.LastUpdatedBy, 
			   ch.Reason, 
			   ut.Description as UserDesc, 
			   '' as ModifyType, 
			   '' as Account, 
			   '' as Amount, 
