CREATE  PROCEDURE [dbo].[admin_GetPaymentHistory]
					@iListType int,
					@sSearch1 varchar(40),
					@sSearch2 varchar(40)=null,
					@sSearch3 varchar(40)=null
AS


create table #p1 (PaymentId char(12))
declare @dtLo datetime, @dtHi datetime
if @iListType = 1
	insert into #p1 values (@sSearch1)
else if @iListType = 2    /* date*/
	begin
	select @dtLo = convert(datetime, @sSearch1),@dtHi = dateadd(day,1,convert(datetime,@sSearch1))
	insert into #p1
	select PaymentId from payment_history where processeddate between
	   @dtLo and @dtHi
	end
else if @iListType = 3
	insert into #p1
	select PaymentId from payment_history where memberid=@sSearch1
else if @iListType = 4    /* date range*/
	begin
	select @dtLo = convert(datetime, @sSearch1), @dtHi = convert(datetime, @sSearch2)
	
	if datepart(hour, @dtHi)=0 and datepart(minute,@dtHi)=0 and datepart(second,@dtHi)=0 
		select @dtHi = dateadd(day,1,@dtHi)
	insert into #p1
	select paymentId from payment_history  with (nolock) where processeddate between @dtLo and @dtHi
	end
else if @iListType = 5 /* by memberid and date range */
	begin
	if @sSearch2 IS NULL and @sSearch3 IS NULL
		insert into #p1
		select paymentId from payment_history with (nolock)  where memberid=@sSearch1
	else if @sSearch2 IS NULL
		begin		
		select @dtHi = convert(datetime, @sSearch3)
		if datepart(hour, @dtHi)=0 and datepart(minute,@dtHi)=0 and datepart(second,@dtHi)=0 
			select @dtHi = dateadd(day,1,@dtHi)
		insert into #p1
		select paymentid from payment_history  with (nolock) where memberid=@sSearch1
			and ProcessedDate < @dtHi
		end
	else if @sSearch3 IS NULL
		begin		
		select @dtLo = convert(datetime, @sSearch2)
		insert into #p1
		select paymentid from payment_history  with (nolock) where memberid=@sSearch1
			and ProcessedDate > @dtLo
		end
	else
		begin
		select @dtLo=convert(datetime,@sSearch2), @dtHi = convert(datetime, @sSearch3)
		if datepart(hour, @dtHi)=0 and datepart(minute,@dtHi)=0 and datepart(second,@dtHi)=0 
			select @dtHi = dateadd(day,1,@dtHi)
		insert into #p1
		select paymentid from payment_history  with (nolock) where memberid=@sSearch1
			and ProcessedDate between @dtLo and @dtHi
		end
	end

if (@@ERROR <> 0) GOTO on_error
	select 	ph.PaymentID            ,
            ph.MemberID,
			ph.PayeeListID          ,
			ph.Account		        ,
			e.name as PayeeName     ,
			s.description as status ,
			'$' + ltrim(convert(char(25), convert(money,convert(real,ph.amount)/100))) as amount,
			ph.UsersAccountAtPayee  ,
			ph.NameOnAccount		,
			ph.CheckNum             ,
			ph.ProcessedDate        ,
			ph.FailedDate           ,
			ph.CancelledDate        ,
			ph.DueDate, 
			ph.RecPaymentID         ,
			ph.Memo                 ,
			ph.BillReference        ,
			ph.PayeeID              ,
			ph.RunID                ,
			ph.MemberFirstname      ,
			ph.MemberLastName       ,
			ph.SourceApplication, 
			ISNULL(RIGHT(ph.TrsfSource, 4), '') as TrsfSource,
			ph.DeliveryDate     
	from payment_history ph  with (nolock) 
		inner join #p1 on ph.paymentid=#p1.paymentid
		inner join payee e  with (nolock) on ph.payeeid=e.PayeeId
		inner join status_code s  with (nolock) on ph.StatusCode=s.StatusCode
	order by ph.ProcessedDate

if (@@ERROR <> 0) GOTO on_error
RETURN (0)
on_error:
	RETURN (-1)



GO
