/*
 ==================================================================
  SP Name:     admin_GetPaymentsFromLoans

  Description: Obtains all payments that have been made or are 
               scheduled to be made from a PLOC account or a credit 
               card account.

  Resources: 	Payment_History
				   Payee
				   Status_Code
				   Payment
				   Recurring_Payment
				   User_Payee
  ------------------------------------------------------------------
  Accepts:		@StartDate  datetime,
               @EndDate    datetime, 
               @MemberID   varchar(32) = ''

  Returns:		
   ==================================================================
   Created:  	9/11/2007
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
	4/28/2013	Added returing the Delivery Date
==================================================================
*/
CREATE  PROCEDURE [dbo].[admin_GetPaymentsFromLoans]
                  @StartDate  datetime,
                  @EndDate    datetime, 
                  @MemberID   varchar(32) = ''
AS

CREATE TABLE #p1 (PaymentId varchar(12))
CREATE TABLE #p2 (PaymentId varchar(12))
CREATE TABLE #p3 (RecPaymentId varchar(12))

IF (DATEPART(hour, @EndDate) = 0) AND (DATEPART(minute, @EndDate) = 0) AND (DATEPART(second, @EndDate) = 0)
	SELECT @EndDate = DATEADD(day, 1, @EndDate)

IF (LEN(RTRIM(@MemberID)) = 0)
BEGIN
   INSERT INTO #p1
      SELECT PaymentID 
         FROM Payment_History WITH (NOLOCK) 
         WHERE ProcessedDate BETWEEN @StartDate AND @EndDate 
            AND ((TrsfSource IS NOT NULL AND Len(TrsfSource) > 0) 
               OR (CHARINDEX('L', Account) > 0))

   INSERT INTO #p2
      SELECT PaymentID 
         FROM Payment p WITH (NOLOCK)  
         WHERE LastUpdateDateTime BETWEEN @StartDate AND @EndDate
            AND ((TrsfSource IS NOT NULL AND Len(TrsfSource) > 0) 
               OR (CHARINDEX('L', Account) > 0))

   INSERT INTO #p3
      SELECT RecPaymentId 
         FROM Recurring_Payment WITH (NOLOCK) 
         WHERE LastUpdateDateTime BETWEEN @StartDate AND @EndDate
            AND ((TrsfSource IS NOT NULL AND Len(TrsfSource) > 0) 
               OR (CHARINDEX('L', Account) > 0))
END
ELSE
BEGIN
   INSERT INTO #p1
      SELECT PaymentID 
         FROM Payment_History WITH (NOLOCK) 
         WHERE ProcessedDate BETWEEN @StartDate AND @EndDate 
            AND MemberID = @MemberID
            AND ((TrsfSource IS NOT NULL AND Len(TrsfSource) > 0) 
               OR (CHARINDEX('L', Account) > 0))

   INSERT INTO #p2
      SELECT PaymentID 
         FROM Payment p WITH (NOLOCK)  
         WHERE LastUpdateDateTime BETWEEN @StartDate AND @EndDate
            AND MemberID = @MemberID
            AND ((TrsfSource IS NOT NULL AND Len(TrsfSource) > 0) 
               OR (CHARINDEX('L', Account) > 0))

   INSERT INTO #p3
      SELECT RecPaymentId 
         FROM Recurring_Payment WITH (NOLOCK) 
         WHERE LastUpdateDateTime BETWEEN @StartDate AND @EndDate
            AND MemberID = @MemberID
            AND ((TrsfSource IS NOT NULL AND Len(TrsfSource) > 0) 
               OR (CHARINDEX('L', Account) > 0))
END


SELECT 	ph.PaymentID,
         ph.RecPaymentID,
         ph.PayeeListID, 
			ph.PayeeID,
			p.Name as PayeeName,
			ph.Account,
			CONVERT(varchar, CONVERT(money, CONVERT(real, ph.amount)/100)) as Amount,
			ph.ProcessedDate,
			ph.DueDate, 
			s.Description as Status,
			ph.BillReference,
			ph.SourceApplication, 
			ph.Memo,         
         ph.MemberID,
			ph.UsersAccountAtPayee,
			ph.NameOnAccount,
			ISNULL(RIGHT(ph.TrsfSource, 4), '') as TrsfSource,      
			ph.CheckNum,
			ph.FailedDate,
			ph.CancelledDate,
			ph.RunID,
			ph.MemberFirstname,
			ph.MemberLastName,
			ph.DeliveryDate 
	FROM Payment_History ph WITH (NOLOCK) INNER JOIN #p1 ON ph.PaymentID = #p1.PaymentID
		INNER JOIN Payee p WITH (NOLOCK) ON ph.PayeeID = p.PayeeID
		INNER JOIN Status_Code s WITH (NOLOCK) ON ph.StatusCode = s.StatusCode
	ORDER BY ph.MemberID, ph.ProcessedDate, ph.PaymentID


SELECT 	p.PaymentID, 
			p.RecPaymentID,
			e.PayeeID,
			e.Name as PayeeName,
			p.Account,
			CONVERT(varchar, CONVERT(money, CONVERT(real, p.amount)/100)) as Amount,
			p.WillProcessDate,
			p.DueDate, 
			s.Description as Status,
			p.BillReference,
			p.SourceApplication,
			p.Memo,
			p.MemberId,
			u.UsersAccountAtPayee,
			u.NameOnAccount,
			p.LastUpdateDateTime, 
			ISNULL(RIGHT(p.TrsfSource, 4), '') as TrsfSource,
			p.DeliveryDate
	FROM Payment p  WITH (NOLOCK) INNER JOIN #p2 ON p.PaymentID = #p2.PaymentID 
		INNER JOIN (User_Payee u WITH (NOLOCK) INNER JOIN Payee e WITH (NOLOCK) ON u.PayeeID = e.PayeeID) 
			ON p.PayeeListId = u.PayeeListId 
		INNER JOIN Status_Code s WITH (NOLOCK) ON p.StatusCode = s.StatusCode
	ORDER BY u.MemberId, p.WillProcessDate, p.PaymentID



SELECT 	r.RecPaymentID,
         p.PayeeID,
		   p.Name as PayeeName,
         r.Account,
         CONVERT(varchar, CONVERT(money, CONVERT(real, r.Amount)/100)) as Amount,
         CONVERT(varchar, CONVERT(money, CONVERT(real, r.InitialAmount)/100)) as InitialAmount,
         CONVERT(varchar, CONVERT(money, CONVERT(real, r.FinalAmount)/100)) as FinalAmount,
         r.NextProcessDate, 
         r.NextDueDate, 	      
	      r.LastProcessedDate,
         r.NumPayments,
         UPPER(r.Frequency) as Frequency,
         CASE r.NumPayments 
	         WHEN -1 THEN r.NumPayments 
	         ELSE (r.NumPayments - r.PaymentsProcessed) 
         END as RemainingPayments,
         r.Active,
         r.SourceApplication, 
         r.Memo, 
         r.MemberId,
         u.UsersAccountAtPayee,
         u.NameOnAccount, 
         r.LastUpdateDateTime,
			ISNULL(RIGHT(r.TrsfSource, 4), '') as TrsfSource,
		 r.DeliveryDate
	FROM Recurring_Payment r WITH (NOLOCK) INNER JOIN #p3 on r.RecPaymentId = #p3.RecPaymentId
		INNER JOIN (User_Payee u WITH (NOLOCK) INNER JOIN Payee p WITH (NOLOCK) ON u.PayeeID = p.PayeeID) 
			   ON u.PayeeListId = r.PayeeListId 
	ORDER BY r.MemberId, r.NextProcessDate, r.RecPaymentId




GO
