/*
 ==================================================================
  SP Name:		admin_GetPendingPayments

  Description:	Obtains the pending payments for the date passed in.

  Resources:	[Payment]
				[Recurring_Payment]
				[Payee]
				[User_Payee]
  ------------------------------------------------------------------
  Accepts:		@date smalldatetime 

  Returns:		pymt.[PaymentID]
				,pymt.[RecPaymentID] 
				,pymt.[MemberID] 
				,ISNULL(pymt.[TrsfSource], '') AS [TrsfSource]  
				,pymt.[Account] 
				,(pymt.[Amount] / 100.0) AS [Amount]
				,p.[PayeeID] 
				,p.[Name]  AS [PayeeName] 
				,p.[ProviderID]  
				,pymt.[PayeeListID] 
				,up.[UsersAccountAtPayee] 
				,up.[NameOnAccount] 
				,pymt.[SourceApplication] 
				,pymt.[WillProcessDate] 
				,pymt.[DueDate] 
				,pymt.[DeliveryDate] 
				,pymt.[Memo] 
				,pymt.[EntryDate] 
				,pymt.[LastUpdateDateTime] 
   ==================================================================
   Created:  	08/28/2013
   By:    		GNordstrom
  ------------------------------------------------------------------
  Modifications: 
==================================================================
*/
CREATE   PROCEDURE [dbo].[admin_GetPendingPayments]
					@date smalldatetime 
AS
SET NOCOUNT ON

DECLARE @RunDays varchar(100), @Holiday bit, @CheckHoliday bit  
DECLARE @startDate smalldatetime, @dayName varchar(25)  

SELECT @RunDays = ConfigValue FROM Configuration WHERE ConfigName = 'RunDays'
SELECT @CheckHoliday = ConfigValue FROM Configuration WHERE ConfigName = 'SkipHoliday' --and CUID = @CUID
SET @startDate = @date
SET @dayName =  DATENAME(dw, @date)

IF EXISTS (SELECT [Date] FROM Holidays WHERE Convert(varchar(8),Date, 112) = Convert(Varchar(8), @date, 112))
	SET @Holiday = 1
ELSE 
	SET @Holiday = 0

WHILE ((CHARINDEX(@dayName, @RunDays) = 0) OR ((@CheckHoliday = 1) AND (@Holiday = 1)))
BEGIN
	SET @date = DATEADD(d, 1, @date)
	SET @dayName =  DATENAME(dw, @date)
	IF EXISTS (SELECT [Date] FROM Holidays WHERE Convert(varchar(8),Date, 112) = Convert(Varchar(8), @date, 112))
		SET @Holiday = 1
	ELSE 
		SET @Holiday = 0
END


SELECT 
	 pymt.[PaymentID]
	,pymt.[RecPaymentID] 
	,pymt.[MemberID] 
	,ISNULL(pymt.[TrsfSource], '') AS [TrsfSource]  
	,pymt.[Account] 
	,(pymt.[Amount] / 100.0) AS [Amount]
	,p.[PayeeID] 
	,p.[Name]  AS [PayeeName] 
	,p.[ProviderID]  
	,pymt.[PayeeListID] 
	,up.[UsersAccountAtPayee] 
	,up.[NameOnAccount] 
	,pymt.[SourceApplication] 
	,pymt.[WillProcessDate] 
	,pymt.[DueDate] 
	,pymt.[DeliveryDate] 
	,pymt.[Memo] 
	,pymt.[EntryDate] 
	,pymt.[LastUpdateDateTime] 
FROM [Payment] pymt WITH (NOLOCK) 
	INNER JOIN ([User_Payee] up WITH (NOLOCK) 
	INNER JOIN [Payee] p WITH (NOLOCK) ON up.[PayeeID] = p.[PayeeID])  
				ON pymt.[PayeeListID] = up.[PayeeListID] AND pymt.[MemberID] = up.[MemberID] 
WHERE pymt.[WillProcessDate] >= @startDate 
		AND pymt.[WillProcessDate] <= @date 
UNION 
SELECT 
	NULL AS [PaymentID]
	,r.[RecPaymentID] 
	,r.[MemberID] 
	,ISNULL(r.[TrsfSource], '') AS [TrsfSource]  
	,r.[Account] 
	,(r.[Amount] / 100.0) AS [Amount]
	,p.[PayeeID] 
	,p.[Name]  AS [PayeeName] 
	,p.[ProviderID]  
	,r.[PayeeListID] 
	,up.[UsersAccountAtPayee] 
	,up.[NameOnAccount] 
	,r.[SourceApplication] 
	,r.[NextProcessDate] AS [WillProcessDate] 
	,r.[NextDueDate] AS [DueDate] 
	,r.[DeliveryDate] 
	,r.[Memo] 
	,r.[EntryDate] 
	,r.[LastUpdateDateTime] 
FROM [Recurring_Payment] r WITH (NOLOCK) 
	INNER JOIN ([User_Payee] up WITH (NOLOCK) 
	INNER JOIN [Payee] p WITH (NOLOCK) ON up.[PayeeID] = p.[PayeeID] )  
				ON r.[PayeeListID] = up.[PayeeListID] AND r.[MemberID] = up.[MemberID] 
WHERE r.[Active] = 'Y' 	
	AND (
		(r.[NextProcessDate] >= @startDate AND r.[NextProcessDate] <= @date)
	 OR	(r.[Frequency] = 'WEEKLY' AND DATEADD(week, 1, r.[NextProcessDate]) >= @startDate AND DATEADD(week, 1, r.[NextProcessDate]) <= @date)
	 OR	((r.[Frequency] = 'WEEKLY' OR r.[Frequency] = 'BIWEEKLY') AND DATEADD(week, 2, r.[NextProcessDate]) >= @startDate AND DATEADD(week, 2, r.[NextProcessDate]) <= @date)  
	 OR	(r.[Frequency] = 'WEEKLY' AND	DATEADD(week, 3, r.[NextProcessDate]) >= @startDate AND DATEADD(week, 3, r.[NextProcessDate]) <= @date)
	 OR	((r.[Frequency] = 'WEEKLY' OR r.[Frequency] = 'BIWEEKLY' OR r.[Frequency] = 'FOURWEEKS') AND DATEADD(week, 4, r.[NextProcessDate]) >= @startDate AND DATEADD(week, 4, r.[NextProcessDate]) <= @date)
	 OR	(r.[Frequency] = 'MONTHLY' AND DATEADD(month, 1, r.[NextProcessDate]) >= @startDate AND DATEADD(month, 1, r.[NextProcessDate]) <= @date))
ORDER BY [PaymentID] 
		,[RecPaymentID]  
	 

SET NOCOUNT OFF

