				r.Active                  ,
				'$' + ltrim(convert(char(25), convert(money,convert(real,r.Amount)/100))) as amount,
				'$' + ltrim(convert(char(25), convert(money,convert(real,r.InitialAmount)/100))) as InitialAmount,
				'$' + ltrim(convert(char(25), convert(money,convert(real,r.FinalAmount)/100))) as FinalAmount,
				r.LastProcessedDate       ,
				r.InitialDueDate          ,
         	r.NextProcessDate, 
         	r.NextDueDate, 
				r.NumPayments             ,
				UPPER(r.Frequency) as Frequency,
				r.LastUpdateDateTime      ,
				r.SourceApplication		, 
				CASE r.NumPayments 
					WHEN -1 THEN r.NumPayments 
					ELSE (r.NumPayments - r.PaymentsProcessed) 
				END as RemainingPayments,
				u.UsersAccountAtPayee,
				u.NameOnAccount, 
				CAST(r.Memo as char(50)) as Memo, 
				ISNULL(r.TrsfSource, '') as TrsfSource, 
				p.ProviderID   
   ==================================================================
   Created:  	?
   By:    		?
  ------------------------------------------------------------------
  Modifications: 
	12/15/2005	GNordstrom	Copied ListRecur to the new proc name.
	12/15/2005	GNordstrom	Added returning the number of remaining payments.
	12/19/2005	GNordstrom  Added returning UsersAccountAtPayee, NameOnAccount 
							      and Memo.
	01/03/2007  GNordstrom  Added return the NextProcessDate field from the 
	                        recurring_payment table.
	01/12/2007  GNordstrom  Added returning the NextDueDate and PayeeListID fields.
	09/04/2007  GNordstrom  Added returning the Payment.TrsfSource and  
	                        Payee.ProviderID fields.
	04/28/2013	HLiou		Added returning the DeliveryDate
==================================================================
*/
CREATE PROCEDURE [dbo].[admin_GetRecurringPayments]
		@iListType int,
		@sSearch1 varchar(40),
		@sSearch2 varchar(40)=null
AS

declare @dtLo datetime, @dtHi datetime

create table #p1 (RecPaymentId char(12))

if @iListType = 1
	insert into #p1 
	select r.RecPaymentId from recurring_payment r  with (nolock) inner join user_payee u  with (nolock) on 
		u.PayeeListId = r.PayeeListId and u.MemberId = r.MemberId
		where u.PayeeId = @sSearch1
else if @iListType = 2
	insert into #p1 
	select r.RecPaymentId from recurring_payment r  with (nolock) where r.MemberId = @sSearch1
else if @iListType = 3
	insert into #p1 values (@sSearch1)
if @iListType = 4    /* date */
	begin
		select @dtLo = convert(datetime, @sSearch1),@dtHi = dateadd(day,1,convert(datetime,@sSearch1))
		insert into #p1
		select r.RecPaymentId from recurring_payment r  with (nolock) where r.LastUpdateDateTime between
			@dtLo and @dtHi
	end
else if @iListType = 5    /* date range*/
	begin
		select @dtLo = convert(datetime, @sSearch1), @dtHi = convert(datetime, @sSearch2)
		
		if datepart(hour, @dtHi)=0 and datepart(minute,@dtHi)=0 and datepart(second,@dtHi)=0 
			select @dtHi = dateadd(day,1,@dtHi)
		insert into #p1
		select r.RecPaymentId from recurring_payment r  with (nolock) where r.LastUpdateDateTime between @dtLo and @dtHi
	end
if (@@ERROR <> 0) GOTO on_error
	
select 
	r.RecPaymentID            ,
	p.PayeeId                 ,
	r.PayeeListId, 
	p.name                    ,
	r.MemberId                ,
	r.Account                 ,
	r.Active                  ,
	'$' + ltrim(convert(char(25), convert(money,convert(real,r.Amount)/100))) as amount,
	'$' + ltrim(convert(char(25), convert(money,convert(real,r.InitialAmount)/100))) as InitialAmount,
	'$' + ltrim(convert(char(25), convert(money,convert(real,r.FinalAmount)/100))) as FinalAmount,
	r.LastProcessedDate       ,
	r.InitialDueDate          ,
	r.NextProcessDate, 
	r.NextDueDate, 
	r.NumPayments             ,
	UPPER(r.Frequency) as Frequency,
	r.LastUpdateDateTime      ,
	r.SourceApplication		, 
	CASE r.NumPayments 
		WHEN -1 THEN r.NumPayments 
		ELSE (r.NumPayments - r.PaymentsProcessed) 
	END as RemainingPayments,
	u.UsersAccountAtPayee,
	u.NameOnAccount, 
	CAST(r.Memo as char(50)) as Memo, 
	ISNULL(r.TrsfSource, '') as TrsfSource, 
	p.ProviderID,
	r.DeliveryDate   
	from recurring_payment r with (nolock)  
		inner join #p1 on r.RecPaymentId = #p1.RecPaymentId
		inner join (user_payee u  with (nolock) 
		inner join payee p with (nolock)  on u.payeeid=p.payeeid) 
			on u.PayeeListId = r.PayeeListId and u.MemberId=r.MemberId
	order by r.RecPaymentId

if (@@ERROR <> 0) GOTO on_error
RETURN (0)
on_error:
	RETURN (-1)




